.md-text-field {
  background: none;
  border: 0;
  line-height: inherit;
  padding: 0;

  &:focus {
    outline: none;
  }

  &:-webkit-autofill,
  &:-webkit-autofill:focus {
    box-shadow: 0 0 0 50px $md-white-base inset;
  }
}

.md-text-field--full-width {
  width: 100%;
}

@if $include-multiline {
  .md-text-field-multiline-container {
    position: relative;
  }

  .md-text-field--multiline {
    padding-bottom: 0;
    padding-top: 0;
    resize: none;
  }

  .md-text-field--multiline-mask {
    overflow: hidden;
    position: absolute;
    visibility: hidden;
    z-index: -1;
  }
}

@if $light-theme {
  @return true;
}

$found: false;
@each $name, $color in $md-color-map {
  @if str-index($name, '-200') != null && str-index($name, '-a-200') == null && $color == $primary-color {
    @return true;
  }
}

@warn "The current primary-color: '#{$primary-color}' is not a valid '-200' suffix. The primary color must be a non-accent -200 color when using the dark theme sliders.";

@return false;

$md-text-color: if($light-theme, $md-light-theme-text-color, $md-dark-theme-text-color);
$md-secondary-text-color: if($light-theme, $md-light-theme-secondary-text-color, $md-dark-theme-secondary-text-color);
