  height: 0;
  position: absolute;
  visibility: hidden;
  width: 0;
  z-index: -1;
}

.md-slider-track {
  $center: ($md-slider-height / 2) - ($md-slider-track-height / 2);
  background: $md-slider-off-color;
  content: '';
  display: block;
  flex-grow: 1;
  flex-shrink: 0;
  height: $md-slider-track-height;
  margin-bottom: $center;
  margin-top: $center;
  position: relative;
}

.md-slider-track--full-width {
  @extend %full-width;
