.md-avatar {
  border: 1px solid if($md-light-theme, $md-light-theme-divider-color, $md-dark-theme-divider-color);
  border-radius: 50%;
  display: inline-block;
  height: $md-avatar-size;
  overflow: hidden;
  text-align: center;
  width: $md-avatar-size;

  .md-icon {
    color: inherit;
  }
}

.md-avatar--icon-sized {
  height: $md-font-icon-size;
  width: $md-font-icon-size;
}

.md-avatar-img {
  height: 100%;
  width: auto;
}

.md-avatar-content {
  align-items: center;
  display: flex;
  font-size: $md-font-icon-size;
  height: 100%;
  justify-content: center;
  width: 100%;
}

@include react-md-theme-avatars($md-avatar-default-background, $md-avatar-default-background, default);

@if $include-suffixes {
  @each $suffix, $colors in $md-avatar-colors {
    @include react-md-theme-avatars(nth($colors, 1), nth($colors, 2), $suffix);
  }
}

$invalid-color: validate-slider-primary-color($primary-color, $light-theme);

$md-slider-on-color: $primary-color;
$md-slider-off-color: if($light-theme, $md-slider-light-theme-off-color, $md-slider-dark-theme-off-color);
$bg-color: if($light-theme, $md-white-base, $md-dark-theme-background-color);

.md-slider-thumb--on,
.md-slider-track-fill {
  background: $md-slider-on-color;
}

@if $light-theme != $md-light-theme {
  .md-slider-track,
  .md-slider-thumb--disabled {
    background: $md-slider-off-color;
  }

  .md-slider-thumb--continuous-off,
  .md-slider-thumb--disabled-mask {
    background: $bg-color;
  }

  .md-slider-thumb--off {
    border-color: $md-slider-off-color;
  }
}

@if $include-discrete {
  @include react-md-theme-discrete-slider($primary-color);
}

@if $primary-color != $md-primary-color {
  .md-floating-label--active {
    color: $primary-color;
  }
}

@if $light-theme != $md-light-theme {
  .md-floating-label {
    color: get-color('secondary', $light-theme);
  }

  .md-floating-label--disabled {
    color: get-color('disabled', $light-theme);
  }
}

@if $error-color != $md-text-field-error-color {
  .md-floating-label--error {
    color: $error-color;
  }
}

@if $light-theme != $md-light-theme {
  .md-password-btn {
    color: get-color('hint', $light-theme);
  }

  .md-password-btn--active {
    color: get-color('secondary', $light-theme);

    &::before {
      background: get-color('secondary', $light-theme);
    }
  }

  .md-password-btn--invisible::after {
    background: get-color('background', $light-theme);
  }
}

$md-icon-color: if($light-theme, $md-light-theme-secondary-text-color, $md-dark-theme-secondary-text-color);

.md-icon {
  color: $md-icon-color;
  font-size: $md-font-icon-size;
  transition-duration: $md-transition-time;
  transition-property: color;
}

$invalid-color: validate-slider-primary-color($primary-color, $light-theme);

$md-slider-on-color: $primary-color;
$md-slider-off-color: if($light-theme, $md-slider-light-theme-off-color, $md-slider-dark-theme-off-color);
$bg-color: if($light-theme, $md-white-base, $md-dark-theme-background-color);
$md-slider-mask-ink-color: rgba($primary-color, $md-slider-mask-ink-opacity);

.md-slider-container {
  display: flex;
  flex-wrap: wrap;

  &:hover {
    cursor: pointer;
  }
}

.md-slider-container--disabled {
  &:hover {
    cursor: initial;
  }
}

.md-slider-label {
  display: block;
  width: 100%;
}

.md-slider-input {
  height: 0;
  position: absolute;
  visibility: hidden;
  width: 0;
  z-index: -1;
}

.md-slider-track {
  $center: ($md-slider-height / 2) - ($md-slider-track-height / 2);
  background: $md-slider-off-color;
  content: '';
  display: block;
  flex-grow: 1;
  flex-shrink: 0;
  height: $md-slider-track-height;
  margin-bottom: $center;
  margin-top: $center;
  position: relative;
}

.md-slider-track--full-width {
  @extend %full-width;
}

.md-slider-track-fill {
  background: $md-slider-on-color;
  height: $md-slider-track-height;
  left: 0;
  margin: 0;
  position: absolute;
  top: 0;
  transition-duration: $md-transition-time;
  transition-property: width;
}

.md-slider-track-fill--dragging {
  transition-property: none;
}

.md-slider-thumb {
  border-radius: 50%;
  content: '';
  display: inline-block;
  height: $md-slider-thumb-size;
  position: absolute;
  top: -(($md-slider-thumb-size / 2) - ($md-slider-track-height / 2));
  transition-duration: $md-transition-time;
  transition-property: left, transform, background;
  width: $md-slider-thumb-size;
  z-index: $md-slider-thumb-z-index;

  &:focus {
    outline: none;
  }
}

.md-slider-thumb--on {
  background: $md-slider-on-color;
}

.md-slider-thumb--active {
  transform: scale($md-slider-thumb-active-scale);
  transform-origin: center;
}

.md-slider-thumb--dragging {
  transition-property: transform, background;
}

.md-slider-thumb--disabled {
  background: $md-slider-off-color;
  transform: scale($md-slider-thumb-disabled-scale);
}

.md-slider-thumb--continuous-off {
  background: $bg-color;
  border: 2px solid;
  border-color: $md-slider-off-color;
  position: relative;
}

.md-slider-thumb--mask {
  transform: scale(1);
  z-index: -1;
}

.md-slider-thumb--mask-inked {
  background: $md-slider-mask-ink-color;
  transform: scale(3.5);
  z-index: $md-slider-thumb-z-index - 1;
}

.md-slider-thumb--mask-disabled {
  background: $bg-color;
  border-radius: initial;
  transform: scale(1.1);
  z-index: $md-slider-thumb-z-index - 1;
}

@if $include-discrete {
  @include react-md-discrete-slider($primary-color, $light-theme);
}

@if $include-editor {
  @include react-md-editable-slider;
}

@if $include-indicators {
  @include react-md-slider-indicators;
}

$invalid-color: validate-slider-primary-color($primary-color, $light-theme);

$md-slider-on-color: $primary-color;
$md-slider-off-color: if($light-theme, $md-slider-light-theme-off-color, $md-slider-dark-theme-off-color);
$bg-color: if($light-theme, $md-white-base, $md-dark-theme-background-color);
$md-slider-discrete-mask-ink-color: rgba($primary-color, $md-slider-discrete-mask-ink-opacity);

.md-slider-thumb--discrete {
  &::after {
    border-left: 7px solid transparent;
    border-right: 7px solid transparent;
    border-top: 7px solid transparent;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    content: '';
    height: 0;
    left: 0;
    position: absolute;
    top: 10px;
    transition-duration: $md-transition-time;
    transition-property: border-top-color, transform;
    width: 0;
  }
}

.md-slider-thumb--discrete-active {
  top: 0;
  transform: scale(2) translate3d(0, -18px, 0);

}

.md-sider-thumb--discrete-on {
  &::after {
    border-top-color: $md-slider-on-color;
  }
}

.md-slider-thumb--discrete-off {
  background: if($light-theme, $md-black-base, $md-white-base);
}

.md-slider-thumb--discrete-active-off {
  background: $md-grey-400;

  &::after {
    border-top-color: $md-grey-400;
  }
}

.md-slider-thumb--discrete-mask-inked {
  transform: scale(2.5);
  transition-duration: $md-slider-discrete-ink-transition-time;
}

.md-slider-thumb--discrete-mask-leaving {
  background: $md-slider-discrete-mask-ink-color;
  transition-duration: $md-slider-discrete-ink-transition-time;
  z-index: $md-slider-thumb-z-index - 1;
}

.md-slider-discrete-value {
  align-items: center;
  color: $md-white-base;
  display: flex;
  font-size: 12px;
  height: $md-slider-thumb-size * 2;
  justify-content: center;
  position: absolute;
  text-align: center;
  transform: translate3d(-7px, -43px, 0);
  transition-duration: $md-transition-time;
  transition-property: left;
  width: $md-slider-thumb-size * 2;
  z-index: $md-slider-thumb-z-index + 1;
}

.md-slider-discrete-tick {
  background: if($light-theme, $md-black-base, $md-white-base);
  content: '';
  height: $md-slider-discrete-tick-height;
  position: absolute;
  top: -(($md-slider-discrete-tick-height / 2) - ($md-slider-track-height / 2));
  z-index: $md-slider-thumb-z-index - 1;
}

.md-slider-discrete-value--dragging {
  transition-property: none;
}

@if $include-media {
  @include react-md-text-field-container-media($include-multiline, $include-floating-label);
}

.md-text-field-container {
  @include react-md-theme-text-field-placeholders(get-color('secondary', $light-theme));

  position: relative;
  vertical-align: top;

  // scss-lint:disable VendorPrefix, PseudoElement
  :-moz-placeholder {
    opacity: 1;
  }

  ::-moz-placeholder {
    opacity: 1;
  }

  :-ms-input-placeholder {
    opacity: 1;
  }

  ::-webkit-input-placeholder {
    opacity: 1;
  }

  &:hover {
    cursor: text;
  }
}

.md-text-field-container--input {
  display: inline-flex;
  flex-direction: column;
  justify-content: flex-end;
  line-height: 1;
}

.md-text-field-container--full-width {
  width: 100%;
}

.md-text-field-container--disabled {
  $color: get-color('disabled', $light-theme);
  @include react-md-theme-text-field-placeholders($color);

  color: $color;

  &:hover {
    cursor: initial;
  }
}

@if $include-multiline {
  .md-text-field-container--multiline {
    display: inline-block;
    transition-duration: $md-transition-time * 2;
    transition-property: height;
  }
}

@if $include-block {
  .md-text-field-container--block {
    align-items: center;
    display: flex;
    flex-direction: row;
    justify-content: flex-start;

    .md-text-field {
      width: 100%;
    }
  }
}

@if $include-icon {
  .md-text-field-container--with-icon {
    align-items: flex-end;
    flex-direction: row;
    flex-wrap: wrap;
    justify-content: flex-start;
  }
}

@if $include-media {
  @include react-md-text-field-divider-media;
}

$md-divider-color: get-color('divider', $light-theme);

.md-text-field-divider {
  background: $md-divider-color;
  border: 0;
  height: $md-text-field-divider-height;
  margin-top: $md-text-field-divider-top-padding;
  width: 100%;

  &::after {
    background: $primary-color;
    content: '';
    display: block;
    height: $md-text-field-divider-active-height;
    transition-duration: $md-transition-time;
    transition-property: width;
    width: 0;
  }
}

.md-text-field-divider--error,
.md-text-field-divider--error::after {
  background: $error-color;
}

.md-text-field-divider--active::after {
  width: 100%;
}

.md-text-field-divider--from-left::after {
  left: 0;
}

.md-text-field-divider--from-center::after {
  margin: auto;
}

.md-text-field-divider--from-right::after {
  margin-left: auto;
  right: 0;
}

@if $include-media {
  @include react-md-text-field-floating-label-media;
}

.md-floating-label {
  color: get-color('secondary', $light-theme);
  cursor: text;
  line-height: 1;
  pointer-events: none;
  position: absolute;
  top: 0;
  transition-duration: $md-transition-time;
  transition-property: transform, font-size, color;
}

.md-floating-label--disabled {
  color: get-color('disabled', $light-theme);
}

.md-floating-label--floating {
  font-size: $md-text-field-active-font-size;
}

.md-floating-label--active {
  color: $primary-color;
}

.md-floating-label--error {
  color: $error-color;
}

.md-floating-label--icon-offset {
  left: 40px;
}

.md-text-field-message-container {
  color: get-color('hint', $light-theme);
  display: flex;
  font-size: $md-text-field-message-font-size;
  justify-content: space-between;
}

.md-text-field-message-container--error {
  color: $error-color;
}

.md-text-field-message-container--full-width {
  width: 100%;
}

.md-text-field-message-container--count-only {
  justify-content: flex-end;
}

@if $include-icon {
  .md-text-field-message-container--left-icon-offset {
    margin-left: 40px;
  }

  .md-text-field-message-container--right-icon-offset {
    margin-right: 40px;
  }
}

.md-text-field-counter {
  display: block;
  flex-shrink: 0;
  padding-left: $md-default-padding;
}

.md-text-field-message {
  flex-grow: 1;
  transition-duration: $md-transition-time;
  transition-property: opacity;
}

.md-text-field-message--inactive {
  opacity: 0;
}

.md-text-field-message--active {
  opacity: 1;
}
