.md-avatar {
  border: 1px solid if($md-light-theme, $md-light-theme-divider-color, $md-dark-theme-divider-color);
  border-radius: 50%;
  display: inline-block;
  height: $md-avatar-size;
  overflow: hidden;
  text-align: center;
  width: $md-avatar-size;

  .md-icon {
    color: inherit;
  }
}

.md-avatar--icon-sized {
  height: $md-font-icon-size;
  width: $md-font-icon-size;
}

.md-avatar-img {
  height: 100%;
  width: auto;
}

.md-avatar-content {
  align-items: center;
  display: flex;
  font-size: $md-font-icon-size;
  height: 100%;
  justify-content: center;
  width: 100%;
}

@include react-md-theme-avatar($md-avatar-default-background, $md-avatar-default-background, default);

@if $include-suffixes {
  @each $suffix, $colors in $md-avatar-colors {
    @include react-md-theme-avatar(nth($colors, 1), nth($colors, 2), $suffix);
  }
}

