context.code:
@if $include-date or $include-time {
  $themed: $light-theme != $md-light-theme;

  @if $primary-color != $md-primary-color {
    @if not $light-theme and $themed {
      .md-picker-header {
        background: get-color('card', false);
      }
    } @else if $light-theme {
      .md-picker-header {
        background: $primary-color;
      }
    }

    @if $include-date {
      .md-calendar-date--btn::after {
        background: $primary-color;
      }
    }

    @if $include-time {
      .md-clock-hand {
        &::before,
        &::after {
          background: $primary-color;
        }
      }
    }
  }

  @if $themed {
    .md-picker-content-container {
      background: if($light-theme, get-color('card', true), get-color('background', false));
    }

    @if $include-date {
      .md-calendar-date {
        color: get-color('secondary', false);

        .md-picker-text--active {
          color: get-color('background', false);
        }
      }
    }

    @if $include-time {
      .md-clock-face {
        background: if($light-theme, $md-grey-100, get-color('card', false));
      }
    }
  }
}

.md-picker-content--clock {
  padding: $md-dialog-padding;
  padding-bottom: $md-dialog-padding / 2;
}

.md-time-periods .md-picker-control {
  display: block;
}

.md-time-period {
  font-size: 16em / $md-font-size-base;
  font-weight: $md-font-medium;
  margin: 0;
  padding: 0;
}

.md-clock-face {
  background: if($light-theme, $md-grey-100, get-color('card', false));
  border-radius: 50%;
  position: relative;
}

.md-clock-hand {
  height: 2px;
  position: absolute;
  transform-origin: 0 0;
  transition-duration: $md-transition-time;
  transition-property: width;
  z-index: 1;

  &--active {
    transition-property: transform, width;
  }

  &::before,
  &::after {
    background: $primary-color;
    border-radius: 50%;
    content: '';
    position: absolute;
  }

  &::before {
    height: $md-clock-hand-ball-size;
    left: -$md-clock-hand-ball-size / 2;
    // no idea why
    top: -2px;
    width: $md-clock-hand-ball-size;
  }

  &::after {
    height: $md-clock-time-size;
    right: -$md-clock-time-size / 2 + $md-clock-padding / 2;
    top: -$md-clock-time-size / 2;
    transition-duration: $md-transition-time;
    transition-property: background, border, transform;
    width: $md-clock-time-size;
  }

  &--minute-hover::after {
    transform: scale($md-clock-hand-ball-scale);
  }
}

.md-clock-time {
  align-items: center;
  display: flex;
  height: $md-clock-time-size;
  justify-content: center;
  position: absolute;
  width: $md-clock-time-size;
  z-index: 8;
}

.md-expansion-panel-list {
  @extend %md-list-unstyled;
}

.md-expansion-panel {
  background: get-color('card', $light-theme);
  transition-duration: $md-transition-time;
  transition-property: margin;

  &--expanded {
    &:not(:first-child) {
      margin-top: $md-expansion-panel-opened-margin;
    }

    &:not(:last-child) {
      margin-bottom: $md-expansion-panel-opened-margin;
    }
  }
}

.md-panel-column:not(:last-child) {
  padding-right: $md-default-padding;
}

.md-panel-header {
  align-items: center;
  display: flex;
  font-size: $md-expansion-panel-font-size;
  height: $md-height;
  justify-content: space-between;
  padding-left: $md-expansion-panel-padding;
  padding-right: $md-expansion-panel-padding;
  transition-duration: $md-transition-time;
  transition-property: background, height;

  &--expanded {
    height: $md-expansion-panel-expanded-height;
  }

  &--focused {
    background: $md-grey-200;
  }
}

.md-panel-content {
  padding: 0 $md-expansion-panel-padding $md-default-padding;
}

.md-panel-secondary-label {
  color: get-color('secondary', $light-theme);
  font-size: $md-expansion-panel-secondary-font-size;
}

$invalid-color: validate-slider-primary-color($primary-color, $light-theme);

$md-slider-on-color: $primary-color;
$md-slider-off-color: if($light-theme, $md-slider-light-theme-off-color, $md-slider-dark-theme-off-color);
$bg-color: if($light-theme, $md-white-base, $md-dark-theme-background-color);
$md-slider-discrete-mask-ink-color: rgba($primary-color, $md-slider-discrete-mask-ink-opacity);

.md-slider-thumb--discrete {
  &::after {
    border-left: 7px solid transparent;
    border-right: 7px solid transparent;
    border-top: 7px solid transparent;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    content: '';
    height: 0;
    left: 0;
    position: absolute;
    top: 10px;
    transition-duration: $md-transition-time;
    transition-property: border-top-color, transform;
    width: 0;
  }
}

.md-slider-thumb--discrete-active {
  top: 0;
  transform: scale(2) translate3d(0, -18px, 0);

}

.md-sider-thumb--discrete-on {
  &::after {
    border-top-color: $md-slider-on-color;
  }
}

.md-slider-thumb--discrete-off {
  background: if($light-theme, $md-black-base, $md-white-base);
}

.md-slider-thumb--discrete-active-off {
  background: $md-grey-400;

  &::after {
    border-top-color: $md-grey-400;
  }
}

.md-slider-thumb--discrete-mask-inked {
  transform: scale(2.5);
  transition-duration: $md-slider-discrete-ink-transition-time;
}

.md-slider-thumb--discrete-mask-leaving {
  background: $md-slider-discrete-mask-ink-color;
  transition-duration: $md-slider-discrete-ink-transition-time;
  z-index: $md-slider-thumb-z-index - 1;
}

.md-slider-discrete-value {
  align-items: center;
  color: $md-white-base;
  display: flex;
  font-size: 12px;
  height: $md-slider-thumb-size * 2;
  justify-content: center;
  position: absolute;
  text-align: center;
  transform: translate3d(-7px, -43px, 0);
  transition-duration: $md-transition-time;
  transition-property: left;
  width: $md-slider-thumb-size * 2;
  z-index: $md-slider-thumb-z-index + 1;
}

.md-slider-discrete-tick {
  background: if($light-theme, $md-black-base, $md-white-base);
  content: '';
  height: $md-slider-discrete-tick-height;
  position: absolute;
  top: -(($md-slider-discrete-tick-height / 2) - ($md-slider-track-height / 2));
  z-index: $md-slider-thumb-z-index - 1;
}

.md-slider-discrete-value--dragging {
  transition-property: none;
}

.md-chip {
  align-items: center;
  background: if($light-theme, $md-chip-light-theme-bg-color, $md-chip-dark-theme-bg-color);
  border: 0;
  border-radius: $md-chip-height / 2;
  display: inline-flex;
  height: $md-chip-height;
  padding-left: $md-chip-default-padding;
  padding-right: $md-chip-default-padding;
  position: relative;
  transition-duration: $md-transition-time;
  transition-property: box-shadow, background;
  vertical-align: top;
  white-space: nowrap;

  &:focus {
    @include md-box-shadow(1);
    outline: none;
  }
}

.md-chip--hover.md-chip--hover {
  background: $md-grey-700;
}

.md-chip-text {
  color: if($light-theme, $md-chip-light-theme-color, $md-chip-dark-theme-color);
  font-size: $md-chip-font-size;
  transition-duration: $md-transition-time;
  transition-property: color;
}

@if $include-avatar or $include-contact {
  @include react-md-chips-avatar;
}

@if $include-remove {
  @include react-md-chips-remove;
}

@if $include-contact {
  @include react-md-chips-contact;
}

// has to go last so it has higher precidence.
.md-chip-text--hover {
  color: $md-white-base;
}

.md-color--text {
  color: get-color('text', $light-theme);
}

.md-color--secondary-text {
  color: get-color('secondary', $light-theme);
}

.md-color--disabled {
  color: get-color('disabled', $light-theme);
}

.md-color--primary {
  color: $primary-color;
}

.md-color--primary-ink {
  .md-ink {
    background: rgba($primary-color, .26);
  }
}

.md-color--secondary {
  color: $secondary-color;
}

.md-color--secondary-ink {
  .md-ink {
    background: rgba($secondary-color, .26);
  }
}

.md-background {
  background: get-color('background', $light-theme);
}

.md-background--card {
  background: get-color('card', $light-theme);
}

.md-background--primary {
  background: $primary-color;
  color: if(lightness($primary-color) > 40, $md-white-base, $md-black-base);
}

.md-background--secondary {
  background: $secondary-color;
  color: if(lightness($secondary-color) > 40, $md-white-base, $md-black-base);
}

@if $include-media {
  @media #{$md-desktop-media} {
    .md-background--primary-hover:hover {
      background: rgba($primary-color, .9);
    }

    .md-background--secondary-hover:hover {
      background: rgba($secondary-color, .9);
    }
  }
}

@if $include-color-class-names {
  @include react-md-color-class-names;
}

@if $elevation < 0 or $elevation > 5 {
  @warn 'The elevation must be a number from 0 - 5. elevation given was `#{$elevation}`';
} @else {
  $top-shadow-color: rgba($md-black-base, .14);
  $middle-shadow-color: rgba($md-black-base, .12);
  $bottom-shadow-color: rgba($md-black-base, if($elevation == 1, .2, .4));

  @if $elevation == 0 {
    box-shadow: none;
  } @else if $elevation == 1 {
    box-shadow: 0 2px 2px 0 $top-shadow-color, 0 1px 5px 0 $middle-shadow-color, 0 3px 1px -2px $bottom-shadow-color;
  } @else if $elevation == 2 {
    box-shadow: 0 4px 5px 0 $top-shadow-color, 0 1px 10px 0 $middle-shadow-color, 0 2px 4px -1px $bottom-shadow-color;
  } @else if $elevation == 3 {
    box-shadow: 0 6px 10px 0 $top-shadow-color, 0 1px 18px 0 $middle-shadow-color, 0 3px 5px -1px $bottom-shadow-color;
  } @else if $elevation == 4 {
    box-shadow: 0 8px 10px 1px $top-shadow-color, 0 3px 14px 2px $middle-shadow-color, 0 5px 5px -3px $bottom-shadow-color;
  } @else if $elevation == 5 {
    box-shadow: 0 16px 24px 2px $top-shadow-color, 0 6px 30px 5px $middle-shadow-color, 0 8px 10px -5px $bottom-shadow-color;
  }
}

