.md-bottom-navigation.md-background--primary {
  background: $bg-color;
}

@if $label-color != $md-white-base {
  .md-bottom-nav-label {
    color: $label-color;
  }
}

@if $ink-color != $md-bottom-navigation-shifting-ink-color {
  .md-bottom-navigation .md-ink {
    background: $ink-color;
  }
