*,
*::before,
*::after {
  box-sizing: border-box;
  // scss-lint:disable ColorVariable
  -webkit-tap-highlight-color: rgba(0, 0, 0, 0);
  transition-timing-function: $md-transition-standard;
}

* {
  font-family: $md-font-family;
  line-height: $md-line-height;
}

html {
  background: if($light-theme, $md-light-theme-background-color, $md-dark-theme-background-color);
  font-size: #{$md-font-size-base}px;
}

body {
  -moz-osx-font-smoothing: grayscale;
  -webkit-font-smoothing: antialiased;
  text-rendering: optimizeLegibility;
}

%md-headline,
%md-title,
%md-subheading-2,
%md-subheading-1,
%md-body-2,
%md-body-1 {
  color: $md-text-color;
}

%md-display-4,
%md-display-3,
%md-display-2,
%md-display-1,
%md-caption {
  color: $md-secondary-text-color;
}

@if $extend-html-tags {
  h1 {
    @extend %md-display-1;
  }

  h2 {
    @extend %md-headline;
  }

  h3 {
    @extend %md-title;
  }

  h4 {
    @extend %md-subheading-2;
  }

  h5 {
    @extend %md-subheading-1;
  }

  h6 {
    @extend %md-body-2;
  }

  p {
    @extend %md-body-1;
  }

  caption {
    @extend %md-caption;
  }
}

.md-display-1 {
  @extend %md-display-1;
}

.md-display-2 {
  @extend %md-display-2;
}

.md-display-3 {
  @extend %md-display-3;
}

.md-display-4 {
  @extend %md-display-4;
}

.md-headline {
  @extend %md-headline;
}

.md-title {
  @extend %md-title;
}

.md-subheading-1 {
  @extend %md-subheading-1;
}

.md-subheading-2 {
  @extend %md-subheading-2;
}

.md-body-1 {
  @extend %md-body-1;
}

.md-body-2 {
  @extend %md-body-2;
}

.md-caption {
  @extend %md-caption;
}

.md-text-left {
  @extend %md-text-left;
}

.md-text-center {
  @extend %md-text-center;
}

.md-text-right {
  @extend %md-text-right;
}

.md-text-justify {
  @extend %md-text-justify;
}

.md-text-capitalize {
  @extend %md-text-capitalize;
}

.md-text-lowercalse {
  @extend %md-text-lowercalse;
}

.md-text-uppercase {
  @extend %md-text-uppercase;
}

.md-text-nowrap {
  @extend %md-text-nowrap;
}

.md-text-no-select {
  @extend %md-text-no-select;
}

.md-font-light {
  @extend %md-font-light;
}

.md-font-regular {
  @extend %md-font-regular;
}

.md-font-medium {
  @extend %md-font-medium;
}

.md-font-bold {
  @extend %md-font-bold;
}

.md-text-container {
  @extend %md-block-centered;

  max-width: $md-typography-max-line-length;
  width: 100%;

  &.md-text-container.md-cell {
    margin-left: auto;
    margin-right: auto;
  }
}

$map: if($light-theme, $md-light-theme-colors, $md-dark-theme-colors);
@if map-has-key($map, $name) == false {
  @warn "The given color: #{$name} is not a valid color in the #{if($light-theme, 'light', 'dark')} theme.";
  @return null;
}

@return map-get($map, $name);

$map: if($light-theme, $md-light-theme-colors, $md-dark-theme-colors);
@if map-has-key($map, $name) == false {
  @warn "The given color: #{$name} is not a valid color in the #{if($light-theme, 'light', 'dark')} theme.";
  @return null;
}

@return map-get($map, $name);

$map: if($light-theme, $md-light-theme-colors, $md-dark-theme-colors);
@if map-has-key($map, $name) == false {
  @warn "The given color: #{$name} is not a valid color in the #{if($light-theme, 'light', 'dark')} theme.";
  @return null;
}

@return map-get($map, $name);

@if $include-inline {
  @if $include-media {
    @include react-md-autocompletes-media;
  }

  .md-autocomplete-suggestion {
    color: get-color('secondary', $light-theme);
    overflow: hidden;
    position: absolute;
    white-space: nowrap;
  }
}

@if $light-theme != $md-light-theme and $include-inline {
  .md-autocomplete-suggestion {
    color: get-color('secondary', $light-theme);
  }
}

@if $light-theme != $md-light-theme {
  .md-avatar {
    border-color: get-color('divider', $light-theme);
  }
}

@if $include-media {
  @include react-md-buttons-media($include-flat, $include-raised);
}

// scss-lint:disable QualifyingElement
a.md-btn {
  text-decoration: none;
}

.md-btn {
  background: transparent;
  border: 0;
  border-radius: 2px;
  position: relative;
  transition-duration: $md-transition-time;
  transition-property: background, color;

  &:focus {
    outline: none;
  }

  .md-icon {
    color: inherit;
  }

  .md-icon-separator {
    height: 100%;
  }
}

.md-btn--hover {
  background: if($light-theme, $md-btn-light-theme-hover-color, $md-btn-dark-theme-hover-color);
}

.md-btn--color-primary-active {
  background: rgba($primary-color, .12);
}

.md-btn--color-secondary-active {
  background: rgba($secondary-color, .12);
}

@if $include-flat or $include-raised {
  .md-btn--text {
    font-weight: $md-font-medium;
    min-width: $md-btn-min-width;
    padding: 8px $md-btn-lr-padding;
    text-transform: uppercase;
    transition-duration: $md-transition-time;
  }
}

@if $include-raised or $include-floating {
  .md-btn--raised {
    @include md-box-shadow(1);

    transition: background $md-transition-time, box-shadow $md-transition-time * 2, color $md-transition-time;
  }

  .md-btn--raised-disabled {
    background: get-color('divider', $light-theme);
  }

  .md-btn--raised-pressed {
    @include md-box-shadow(2);
  }
}

@if $include-icon or $include-floating {
  .md-btn--icon {
    border-radius: 50%;
    color: get-color('icon', $light-theme);
    height: $md-btn-icon-size;
    padding: $md-btn-icon-padding;
    width: $md-btn-icon-size;
  }
}

@if $include-floating {
  .md-btn--floating {
    @include md-box-shadow(2);

    height: $md-btn-floating-size;
    padding: ($md-btn-floating-size - $md-font-icon-size) / 2;
    transition-property: background, box-shadow, color;
    width: $md-btn-floating-size;
  }

  .md-btn--floating-pressed {
    @include md-box-shadow(4);
  }

  .md-btn--floating-mini {
    height: $md-btn-floating-mini-size;
    padding: ($md-btn-floating-mini-size - $md-font-icon-size) / 2;
    width: $md-btn-floating-mini-size;
  }

  .md-btn--fixed {
    position: fixed;
    z-index: $md-btn-fixed-z-index;
  }
}

@if $light-theme != $md-light-theme {
  @if $include-flat or $include-raised {
    .md-btn--raised-disabled {
      background: get-color('divider', $light-theme);
    }
  }

  @if $include-icon or $include-floating {
    .md-btn--icon {
      color: get-color('icon', $light-theme);
    }
  }
}

@if $primary-color != $md-primary-color {
  .md-btn--color-primary-active {
    background: rgba($primary-color, .12);
  }
}

@if $secondary-color != $md-secondary-color {
  .md-btn--color-secondary-active {
    background: rgba($secondary-color, .12);
  }
}

.md-color--text {
  color: get-color('text', $light-theme);
}

.md-color--secondary-text {
  color: get-color('secondary', $light-theme);
}

.md-color--disabled {
  color: get-color('disabled', $light-theme);
}

.md-color--primary {
  color: $primary-color;
}

.md-color--primary-ink {
  .md-ink {
    background: rgba($primary-color, .26);
  }
}

.md-color--secondary {
  color: $secondary-color;
}

.md-color--secondary-ink {
  .md-ink {
    background: rgba($secondary-color, .26);
  }
}

.md-background {
  background: get-color('background', $light-theme);
}

.md-background--card {
  background: get-color('card', $light-theme);
}

.md-background--primary {
  background: $primary-color;
  color: if(lightness($primary-color) > 40, $md-white-base, $md-black-base);
}

.md-background--secondary {
  background: $secondary-color;
  color: if(lightness($secondary-color) > 40, $md-white-base, $md-black-base);
}

@if $include-media {
  @media #{$md-desktop-media} {
    .md-background--primary-hover:hover {
      background: rgba($primary-color, .9);
    }

    .md-background--secondary-hover:hover {
      background: rgba($secondary-color, .9);
    }
  }
}

@if $include-color-class-names {
  @include react-md-color-class-names;
}

@if $primary-color != $md-primary-color {
  .md-color--primary {
    color: $primary-color;

  }

  .md-color--primary-ink {
    .md-ink {
      background: rgba($primary-color, .26);
    }
  }

  .md-background--primary {
    background: $primary-color;
  }
}

@if $secondary-color != $md-secondary-color {
  .md-color--secondary {
    color: $secondary-color;
  }

  .md-color--secondary-ink {
    .md-ink {
      background: rgba($secondary-color, .26);
    }
  }

  .md-background--secondary {
    background: $secondary-color;
  }
}

@if $light-theme != $md-light-theme {
  .md-color--text {
    color: get-color('text', $light-theme);
  }

  .md-color--secondary-text {
    color: get-color('secondary', $light-theme);
  }

  .md-color--disabled {
    color: get-color('disabled', $light-theme);
  }

  .md-background {
    background: get-color('background', $light-theme);
  }

  .md-background--card {
    background: get-color('card', $light-theme);
  }
}

@if $include-media and ($primary-color != $md-primary-color or $secondary-color != $md-secondary-color) {
  @media #{$md-desktop-media} {
    @if $primary-color != $md-primary-color {
      .md-background--primary-hover:hover {
        background: rgba($primary-color, .9);
      }
    }

    @if $secondary-color != $md-secondary-color {
      .md-background--secondary-hover:hover {
        background: rgba($secondary-color, .9);
      }
    }
  }
}

@if $light-theme != $md-light-theme {
  thead .md-table-row,
  tbody .md-table-row:not(:last-child) {
    border-bottom-color: get-color('divider', $light-theme);
  }

  .md-table-row {

    &--active {
      background: if($light-theme, $md-data-table-selected-color, $md-data-table-dark-theme-selected-color);
    }

    &--hover {
      background: if($light-theme, $md-data-table-hover-color, $md-data-table-dark-theme-hover-color);
    }
  }
}

thead .md-table-row,
tbody .md-table-row:not(:last-child) {
  border-bottom: 1px solid get-color('divider', $light-theme);
}

tbody .md-table-row {
  transition-duration: $md-transition-time;
  transition-property: background;

  &--active {
    background: if($light-theme, $md-data-table-selected-color, $md-data-table-dark-theme-selected-color);
  }

  &--hover {
    background: if($light-theme, $md-data-table-hover-color, $md-data-table-dark-theme-hover-color);
  }
}

.md-divider {
  background: get-color('divider', $light-theme);
  border: 0;
  content: '';
  display: block;
  height: $md-divider-height;
  margin: 0;
}

.md-divider--vertical {
  height: 100%;
  width: $md-divider-height;
}

.md-divider--inset {
  margin-left: $md-list-left-padding;
}

@if $include-borders {
  @include react-md-divider-borders($light-theme);
}

.md-divider-border {
  border-color: get-color('divider', $light-theme);
  border-style: solid;
  border-width: 0;
}

.md-divider-border--top {
  border-top-width: 1px;
}

.md-divider-border--right {
  border-right-width: 1px;
}

.md-divider-border--bottom {
  border-bottom-width: 1px;
}

.md-divider-border--left {
  border-left-width: 1px;
}

@if $light-theme != $md-light-theme {
  .md-divider {
    background: get-color('divider', $light-theme);
  }
}

@if $include-borders {
  @include react-md-theme-divider-borders($light-theme);
}

@if $light-theme != $md-light-theme {
  .md-divider-border {
    border-color: get-color('divider', $light-theme);
  }
}

.md-expansion-panel-list {
  @extend %md-list-unstyled;
}

.md-expansion-panel {
  background: get-color('card', $light-theme);
  transition-duration: $md-transition-time;
  transition-property: margin;

  &--expanded {
    &:not(:first-child) {
      margin-top: $md-expansion-panel-opened-margin;
    }

    &:not(:last-child) {
      margin-bottom: $md-expansion-panel-opened-margin;
    }
  }
}

.md-panel-column:not(:last-child) {
  padding-right: $md-default-padding;
}

.md-panel-header {
  align-items: center;
  display: flex;
  font-size: $md-expansion-panel-font-size;
  height: $md-height;
  justify-content: space-between;
  padding-left: $md-expansion-panel-padding;
  padding-right: $md-expansion-panel-padding;
  transition-duration: $md-transition-time;
  transition-property: background, height;

  &--expanded {
    height: $md-expansion-panel-expanded-height;
  }

  &--focused {
    background: $md-grey-200;
  }
}

.md-panel-content {
  padding: 0 $md-expansion-panel-padding $md-default-padding;
}

.md-panel-secondary-label {
  color: get-color('secondary', $light-theme);
  font-size: $md-expansion-panel-secondary-font-size;
}

@if $light-theme != $md-light-theme {
  .md-expansion-panel {
    background: get-color('card', $light-theme);
  }

  .md-panel-secondary-label {
    color: get-color('secondary', $light-theme);
  }
}

.md-ink-container {
  border-radius: inherit;
  height: 100%;
  left: 0;
  overflow: hidden;
  pointer-events: none;
  position: absolute;
  top: 0;
  width: 100%;
  z-index: 1;
}

.md-ink-container--2x {
  height: 200%;
  left: -50%;
  top: -50%;
  width: 200%;
}

.md-ink {
  background: get-color('divider', $light-theme);
  border-radius: 50%;
  display: block;
  opacity: 1;
  position: absolute;
  transform: scale(0);
  z-index: -1;
}

.md-ink--active {
  transition-duration: $md-ink-enter-transition-time;
  transition-property: opacity, transform;
  transition-timing-function: $md-transition-acceleration;
}

.md-ink--expanded {
  transform: scale(.9);
}

.md-ink--pulsing {
  animation-duration: $md-ink-pulse-time;
  animation-iteration-count: infinite;
  animation-name: md-ink-pulse;
  animation-timing-function: $md-transition-standard;
}

.md-ink--leaving {
  opacity: 0;
  transition-duration: .3s;
  transition-timing-function: $md-transition-deceleration;
}

@keyframes md-ink-pulse {
  0% {
    transform: scale(.9);
  }

  66% {
    transform: scale(.85);
  }

  100% {
    transform: scale(.9);
  }
}

@if $light-theme != $md-light-theme {
  .md-ink {
    background: get-color('divider', $light-theme);
  }
}

@if $include-media {
  @include react-md-lists-media($include-icon, $include-avatar, $include-two-lines, $include-three-lines);
}

.md-list {
  @extend %md-list-unstyled;
  background: get-color('card', $light-theme);
  user-select: none;

  .md-avatar,
  .md-icon {
    flex-shrink: 0;
  }

  .md-divider {
    margin-bottom: 8px;
    margin-top: 8px;
  }
}

.md-list-tile {
  align-items: center;
  display: flex;
  padding-left: $md-default-padding;
  padding-right: $md-default-padding;
  text-decoration: none;
  transition-duration: $md-transition-time;
  transition-property: background;
}

.md-list-tile--active {
  background: get-color('hover', $light-theme);
}

.md-list-item--inset {
  padding-left: $md-list-left-padding;
}

.md-tile-content {
  flex-grow: 1;
}

@if $include-icon {
  .md-tile-content--left-icon {
    padding-left: $md-list-left-padding - $md-font-icon-size - $md-default-padding;
  }
}

@if $include-avatar {
  .md-tile-content--left-avatar {
    padding-left: $md-list-left-padding - $md-avatar-size - $md-default-padding;
  }
}

@if $include-icon or $include-avatar {
  .md-tile-addon--icon {
    height: $md-font-icon-size;
  }

  .md-color--primary .md-icon {
    color: inherit;
  }

  .md-tile-addon--avatar {
    height: $md-avatar-size;
  }

  .md-tile-content--right-padding {
    padding-right: $md-default-padding;
  }
}

%tile-text {
  display: block;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.md-tile-text--primary {
  @extend %tile-text;
}

@if $include-two-lines or $include-three-lines {
  .md-tile-text--secondary {
    @extend %tile-text;
  }
}

@if $include-three-lines {
  .md-tile-text--three-lines {
    // scss-lint:disable PropertySortOrder
    white-space: pre-line;

    // Magic with webkit-line clamp
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 2;
  }

  @if $include-icon or $include-avatar {
    .md-list-tile--three-lines .md-tile-addon {
      align-self: flex-start;
    }
  }
}

@if $nested-items-depth != null and $nested-items-depth > 0 {
  @include react-md-lists-nested($nested-items-depth, $nested-items-increment);
}

@if $include-controls {
  .md-list-control {
    flex-grow: 1;

    .md-selection-control-label {
      width: 100%;
    }
  }

  .md-tile-content--left-button {
    padding-left: $md-list-left-padding - $md-font-icon-size * 2;
  }

  .md-list-control--right {
    justify-content: flex-end;
  }

  .md-list-tile--control-left {
    padding-left: 0;
  }

  .md-list-tile--control-right {
    padding-right: 0;
  }
}

@if $light-theme != $md-light-theme {
  .md-list {
    background: get-color('card', $light-theme);
  }


  .md-list-tile--active {
    background: get-color('hover', $light-theme);
  }
}

.md-media-overlay {
  background: $md-media-overlay-color;
  bottom: 0;
  position: absolute;
  width: 100%;

  .md-btn,
  .md-color--text {
    color: get-color('text', false);
  }

  .md-color--secondary-text {
    color: get-color('secondary', false);
  }
}

@if $include-date or $include-time {
  $themed: $light-theme != $md-light-theme;

  @if $primary-color != $md-primary-color {
    @if not $light-theme and $themed {
      .md-picker-header {
        background: get-color('card', false);
      }
    } @else if $light-theme {
      .md-picker-header {
        background: $primary-color;
      }
    }

    @if $include-date {
      .md-calendar-date--btn::after {
        background: $primary-color;
      }
    }

    @if $include-time {
      .md-clock-hand {
        &::before,
        &::after {
          background: $primary-color;
        }
      }
    }
  }

  @if $themed {
    .md-picker-content-container {
      background: if($light-theme, get-color('card', true), get-color('background', false));
    }

    @if $include-date {
      .md-calendar-date {
        color: get-color('secondary', false);

        .md-picker-text--active {
          color: get-color('background', false);
        }
      }
    }

    @if $include-time {
      .md-clock-face {
        background: if($light-theme, $md-grey-100, get-color('card', false));
      }
    }
  }
}

.md-picker-container {
  position: relative;
}

.md-picker-content-container {
  background: if($light-theme, get-color('card', true), get-color('background', false));
}

.md-picker--inline {
  @include md-box-shadow(3);

  position: absolute;
  z-index: $md-picker-inline-z-index;
}

.md-picker--inline-icon {
  left: $md-font-icon-size + $md-default-padding;
}

.md-picker-control {
  @extend %md-text-left;

  padding: 0;
}

.md-picker-text {
  color: get-color('secondary', false);

  &.md-picker-text > * {
    color: inherit;
  }

  > * {
    font-weight: inherit;
    margin: 0;
    transition-duration: $md-transition-time;
    transition-property: color, font;
  }
}

.md-picker-text--active {
  color: get-color('text', false);
  font-weight: $md-font-medium;
}

.md-dialog--picker {
  max-height: calc(100vh - #{$md-picker-mobile-margin});
  max-width: calc(100vw - #{$md-picker-mobile-margin});
  width: auto;
}

.md-dialog-content--picker {
  max-height: inherit;
  max-width: inherit;
  padding: 0;
}

.md-picker-header {
  background: if($light-theme, $primary-color, get-color('card', false));
  padding: $md-dialog-padding;
}

@if $include-forceful-classes {
  @include react-md-pickers-force-orientation;
}

.md-picker--date {
  .md-picker-control {
    display: block;
  }
}

.md-picker-content--calendar {
  padding-left: 12px;
  padding-right: 12px;
}

.md-calendar-controls {
  align-items: center;
  display: flex;

  .md-title {
    flex-grow: 1;
    font-size: 16px;
    margin-bottom: 0;
    text-align: center;
  }
}

.md-calendar-date {
  @extend %md-inline-block;
  @extend %md-text-center;

  @if not $light-theme {
    color: get-color('secondary', false);

    .md-picker-text--active {
      color: get-color('background', false);
    }
  }

  margin: 0;
  width: calc(100% / 7);
}

.md-picker-content--year {
  overflow-y: auto;
}

.md-years {
  list-style: none;
  margin: 0;
  padding: 0;
}

.md-year {
  font-size: $md-year-font-size;
  padding: 12px;
  transition-duration: $md-transition-time;
  transition-property: color, font-size;

  &:focus:not(.md-year--active) {
    font-size: $md-year-focus-font-size;
  }

  &--active {
    font-size: $md-year-active-font-size;
    font-weight: $md-font-medium;
  }
}

.md-calendar-date--btn {

  &::after {
    background: $primary-color;
    border-radius: 50%;
    content: '';
    display: block;
    left: 50%;
    position: absolute;
    top: 50%;
    transform: translateX(-50%) translateY(-50%) scale(0);
    transition-duration: $md-transition-time;
    transition-property: transform;
    z-index: 0;
  }
}

.md-calendar-date--today {
  color: $primary-color;
  font-weight: $md-font-bold;
}

.md-calendar-date--btn-active {
  font-weight: $md-font-bold;

  &::after {
    transform: translateX(-50%) translateY(-50%) scale(1);
  }
}

.md-calendar-date--date {
  position: relative;
  z-index: 1;
}

.md-calendar-dow {
  font-size: $md-calendar-date-font-size;
  font-weight: $md-font-bold;
  line-height: $md-line-height;
}

.md-picker-content--clock {
  padding: $md-dialog-padding;
  padding-bottom: $md-dialog-padding / 2;
}

.md-time-periods .md-picker-control {
  display: block;
}

.md-time-period {
  font-size: 16em / $md-font-size-base;
  font-weight: $md-font-medium;
  margin: 0;
  padding: 0;
}

.md-clock-face {
  background: if($light-theme, $md-grey-100, get-color('card', false));
  border-radius: 50%;
  position: relative;
}

.md-clock-hand {
  height: 2px;
  position: absolute;
  transform-origin: 0 0;
  transition-duration: $md-transition-time;
  transition-property: width;
  z-index: 1;

  &--active {
    transition-property: transform, width;
  }

  &::before,
  &::after {
    background: $primary-color;
    border-radius: 50%;
    content: '';
    position: absolute;
  }

  &::before {
    height: $md-clock-hand-ball-size;
    left: -$md-clock-hand-ball-size / 2;
    // no idea why
    top: -2px;
    width: $md-clock-hand-ball-size;
  }

  &::after {
    height: $md-clock-time-size;
    right: -$md-clock-time-size / 2 + $md-clock-padding / 2;
    top: -$md-clock-time-size / 2;
    transition-duration: $md-transition-time;
    transition-property: background, border, transform;
    width: $md-clock-time-size;
  }

  &--minute-hover::after {
    transform: scale($md-clock-hand-ball-scale);
  }
}

.md-clock-time {
  align-items: center;
  display: flex;
  height: $md-clock-time-size;
  justify-content: center;
  position: absolute;
  width: $md-clock-time-size;
  z-index: 8;
}

.md-tab {
  align-items: center;
  color: inherit;
  display: flex;
  flex-direction: column;
  flex-grow: 1;
  flex-shrink: 0;
  font-weight: $md-font-medium;
  height: $md-tab-height;
  justify-content: flex-end;
  overflow: hidden;
  padding-bottom: $md-tab-single-line-padding-bottom;
  padding-left: $md-tab-padding;
  padding-right: $md-tab-padding;
  position: relative;
  text-align: center;
  text-decoration: none;
  text-transform: uppercase;

  &--multiline {
    font-size: $md-tab-multiline-font-size;
    padding-bottom: $md-tab-padding;
  }

  @if $include-icons {
    &--icon {
      height: $md-tab-icon-height;
      padding-bottom: $md-tab-icon-padding-bottom;
    }
  }

  &--inactive {
    color: get-color('secondary', false);
  }
}

@if $light-theme != $md-light-theme {
  .md-text-field-container {
    @include react-md-theme-placeholders(get-color('secondary', $light-theme));
  }
}

@include react-md-theme-text-field-divider($primary-color, $error-color);

@if $include-floating-label {
  @include react-md-theme-text-field-floating-label($primary-color, $light-theme, $error-color);
}

@if $include-icon or $include-password {
  @include react-md-theme-text-field-icon($primary-color, $light-theme, $error-color);
}

@if $include-message {
  @include react-md-theme-text-field-message($light-theme, $error-color);
}

.md-text-field-container {
  @include react-md-theme-placeholders(get-color('secondary', $light-theme));

  position: relative;

  // scss-lint:disable VendorPrefix, PseudoElement
  :-moz-placeholder {
    opacity: 1;
  }

  ::-moz-placeholder {
    opacity: 1;
  }

  :-ms-input-placeholder {
    opacity: 1;
  }

  ::-webkit-input-placeholder {
    opacity: 1;
  }
}

.md-text-field-container--input {
  line-height: 1;
}

@if $include-block {
  .md-text-field-container--input-block {
    display: flex;
  }
}

@if $include-multiline {
  .md-text-field-multiline-container,
  .md-text-field-container--multiline {
    transition-duration: $md-transition-time;
    transition-property: height;
  }

  .md-text-field-multiline-container {
    position: relative;
  }
}

.md-floating-label {
  color: get-color('secondary', $light-theme);
  cursor: text;
  line-height: 1;
  pointer-events: none;
  position: absolute;
  top: 0;
  transition-duration: $md-transition-time;
  transition-property: transform, font-size, color;
  white-space: nowrap;
}

.md-floating-label--disabled {
  color: get-color('disabled', $light-theme);
}

.md-floating-label--floating {
  font-size: $md-text-field-active-font-size;
}

.md-floating-label--active {
  color: $primary-color;
}

.md-floating-label--error {
  color: $error-color;
}

.md-floating-label--icon-offset {
  left: 40px;
}

@if $primary-color != $md-primary-color {
  .md-floating-label--active {
    color: $primary-color;
  }
}

@if $light-theme != $md-light-theme {
  .md-floating-label {
    color: get-color('secondary', $light-theme);
  }

  .md-floating-label--disabled {
    color: get-color('disabled', $light-theme);
  }
}

@if $error-color != $md-text-field-error-color {
  .md-floating-label--error {
    color: $error-color;
  }
}

.md-text-field-message-container {
  color: get-color('hint', $light-theme);
  display: flex;
  font-size: $md-text-field-message-font-size;
  justify-content: space-between;
}

.md-text-field-message-container--error {
  color: $error-color;
}

.md-text-field-message-container--count-only {
  justify-content: flex-end;
}

@if $include-icon {
  .md-text-field-message-container--left-icon-offset {
    margin-left: 40px;
  }

  .md-text-field-message-container--right-icon-offset {
    margin-right: 40px;
  }
}

.md-text-field-message {
  flex-grow: 1;
  transition-duration: $md-transition-time;
  transition-property: opacity;
}

.md-text-field-message--counter {
  display: block;
  flex-grow: initial;
  flex-shrink: 0;
  padding-left: $md-default-padding;
}

.md-text-field-message--inactive {
  opacity: 0;
}

.md-text-field-message--active {
  opacity: 1;
}

@if $light-theme != $md-light-theme {
  .md-text-field-message-container {
    color: get-color('hint', $light-theme);
  }
}

@if $error-color != $md-text-field-error-color {
  .md-text-field-message-container--error {
    color: $error-color;
  }
}

.md-text-field-divider-container {
  display: inline-block;
}

.md-text-field-divider-container--grow {
  display: block;
  flex-grow: 1;
}

.md-text-field-icon + .md-text-field-divider-container,
.md-text-field-divider-container + .md-text-field-icon {
  margin-left: 16px;
}

.md-text-field-icon--disabled {
  color: get-color('disabled', $light-theme);
}

.md-text-field-icon--active {
  color: $primary-color;
}

.md-text-field-icon--error {
  color: $error-color;
}

.md-text-field-icon-container {
  align-items: flex-end;
  display: flex;
  width: 100%;
}

@if $primary-color != $md-primary-color {
  .md-text-field-icon--active {
    color: $primary-color;
  }
}

@if $light-theme != $md-light-theme {
  .md-text-field-icon--disabled {
    color: get-color('disabled', $light-theme);
  }
}

@if $error-color != $md-text-field-error-color {
  .md-text-field-icon--error {
    color: $error-color;
  }
}

.md-password-btn {
  background: transparent;
  border: 0;
  color: get-color('hint', $light-theme);
  height: $md-font-icon-size;
  padding: 0;
  width: $md-font-icon-size;

  &:focus {
    outline: none;
  }

  .md-icon {
    color: inherit;
  }
}

.md-password-btn--active {
  color: get-color('secondary', $light-theme);

  &::before {
    background: get-color('secondary', $light-theme);
  }
}

.md-password-btn--invisible {
  &::before,
  &::after {
    content: '';
    display: block;
    height: 2px;
    position: absolute;
    top: 50%;
    width: 100%;
  }

  &::before {
    transform: translate3d(-1px, -50%, 0) rotate3d(0, 0, 1, 45deg);
  }

  &::after {
    background: get-color('background', $light-theme);
    transform: translate3d(1px, -50%, 0) rotate3d(0, 0, 1, 45deg);
  }
}

@if $light-theme != $md-light-theme {
  .md-password-btn {
    color: get-color('hint', $light-theme);
  }

  .md-password-btn--active {
    color: get-color('secondary', $light-theme);

    &::before {
      background: get-color('secondary', $light-theme);
    }
  }

  .md-password-btn--invisible::after {
    background: get-color('background', $light-theme);
  }
}

@if $include-media {
  @include react-md-toolbars-media($include-prominent, $include-btn-text, $include-autocompletes, $include-select-fields);
}

.md-toolbar {
  display: flex;

  &--fixed {
    left: 0;
    position: fixed;
    top: 0;
    width: 100vw;
    z-index: $md-toolbar-z-index;
  }

  &--inset {
    margin: $md-toolbar-inset-margin;
    max-width: calc(100vw - #{$md-toolbar-inset-margin * 2});
  }
}

@if $include-themed {
  .md-toolbar--themed {
    background: get-color('toolbar', $light-theme);

    .md-title {
      color: if($light-theme, get-color('text', $light-theme), $md-white-base);
    }
  }
}

.md-title--toolbar {
  color: if($light-theme, get-color('text', $light-theme), $md-white-base);
  margin-bottom: 0;
  transition-duration: $md-transition-time;
  transition-property: transform;
}

@if $include-prominent and $md-toolbar-prominent-title-font-size != $md-title-font-size {
  .md-title--toolbar-prominent {
    font-size: $md-toolbar-prominent-title-font-size;
  }
}

.md-toolbar--text-white {
  color: $md-white-base;

  $selectors: ('btn' 'icon' 'title--toolbar');
  @if $include-text-fields or $include-select-fields or $include-autocompletes {
    $selectors: append($selectors, 'text-field');
  }

  $color-selector: '';

  @each $selector in $selectors {
    $color-selector: $color-selector + if($color-selector != '', ', ', '');
    $color-selector: $color-selector + '.md-#{$selector}';
  }

  #{$color-selector} {
    color: inherit;
  }

  @if $include-text-fields or $include-autocompletes {
    @include react-md-theme-placeholders($md-white-base);
  }
}

@if $include-text-fields or $include-select-fields or $include-autocompletes {
  .md-toolbar {
    .md-text-field-container {
      padding-bottom: 0;
      padding-top: 0;
    }

    .md-text-field {
      font-size: $md-title-font-size;
    }
  }
}

@if $include-autocompletes {
  .md-toolbar {
    .md-autocomplete {
      height: 100%;
    }
  }
}

@if $include-select-fields {
  .md-toolbar {
    .md-menu-container {
      padding-left: 0;
      padding-right: 0;
    }

    .md-select-field-container {
      height: 100%;
    }
  }
}

@if $light-theme != $md-light-theme {
  @if $include-themed {
    .md-toolbar--themed {
      background: get-color('toolbar', $light-theme);

      .md-title {
        color: if($light-theme, get-color('text', $light-theme), $md-white-base);
      }
    }
  }
}

$md-text-color: get-color('text', $light-theme);
$md-secondary-text-color: get-color('secondary', $light-theme);
$md-hint-text-color: get-color('hint', $light-theme);
$md-disabled-text-color: get-color('disabled', $light-theme);

@if $include-media {
  @include react-md-typography-media($extend-html-tags);
}

*,
*::before,
*::after {
  box-sizing: border-box;
  // scss-lint:disable ColorVariable
  -webkit-tap-highlight-color: rgba(0, 0, 0, 0);
  transition-timing-function: $md-transition-standard;
}

* {
  font-family: $md-font-family;
  line-height: $md-line-height;
}

html {
  background: if($light-theme, $md-light-theme-background-color, $md-dark-theme-background-color);
  font-size: #{$md-font-size-base}px;
}

body {
  -moz-osx-font-smoothing: grayscale;
  -webkit-font-smoothing: antialiased;
  text-rendering: optimizeLegibility;
}

%md-headline,
%md-title,
%md-subheading-2,
%md-subheading-1,
%md-body-2,
%md-body-1 {
  color: $md-text-color;
}

%md-display-4,
%md-display-3,
%md-display-2,
%md-display-1,
%md-caption {
  color: $md-secondary-text-color;
}

@if $extend-html-tags {
  h1 {
    @extend %md-display-1;
  }

  h2 {
    @extend %md-headline;
  }

  h3 {
    @extend %md-title;
  }

  h4 {
    @extend %md-subheading-2;
  }

  h5 {
    @extend %md-subheading-1;
  }

  h6 {
    @extend %md-body-2;
  }

  p {
    @extend %md-body-1;
  }

  caption {
    @extend %md-caption;
  }
}

.md-display-1 {
  @extend %md-display-1;
}

.md-display-2 {
  @extend %md-display-2;
}

.md-display-3 {
  @extend %md-display-3;
}

.md-display-4 {
  @extend %md-display-4;
}

.md-headline {
  @extend %md-headline;
}

.md-title {
  @extend %md-title;
}

.md-subheading-1 {
  @extend %md-subheading-1;
}

.md-subheading-2 {
  @extend %md-subheading-2;
}

.md-body-1 {
  @extend %md-body-1;
}

.md-body-2 {
  @extend %md-body-2;
}

.md-caption {
  @extend %md-caption;
}

.md-text-left {
  @extend %md-text-left;
}

.md-text-center {
  @extend %md-text-center;
}

.md-text-right {
  @extend %md-text-right;
}

.md-text-justify {
  @extend %md-text-justify;
}

.md-text-capitalize {
  @extend %md-text-capitalize;
}

.md-text-lowercalse {
  @extend %md-text-lowercalse;
}

.md-text-uppercase {
  @extend %md-text-uppercase;
}

.md-text-nowrap {
  @extend %md-text-nowrap;
}

.md-text-no-select {
  @extend %md-text-no-select;
}

.md-font-light {
  @extend %md-font-light;
}

.md-font-regular {
  @extend %md-font-regular;
}

.md-font-medium {
  @extend %md-font-medium;
}

.md-font-bold {
  @extend %md-font-bold;
}

.md-text-container {
  @extend %md-block-centered;

  max-width: $md-typography-max-line-length;
  width: 100%;

  &.md-text-container.md-cell {
    margin-left: auto;
    margin-right: auto;
  }
}

@if $light-theme != $md-light-theme {
  .#{$html-class-name} {
    background: get-color('background', $light-theme);

    .md-headline,
    .md-title,
    .md-subheading-2,
    .md-subheading-1,
    .md-body-2,
    .md-body-1 {
      color: get-color('text', $light-theme);
    }

    .md-display-4,
    .md-display-3,
    .md-display-2,
    .md-display-1,
    .md-caption {
      color: get-color('secondary', $light-theme);
    }

    @if $extend-html-tags {
      h6,
      h5,
      h4,
      h3,
      h2,
      h1,
      p {
        color: get-color('text', $light-theme);
      }

      h1,
      caption {
        color: get-color('secondary', $light-theme);
      }

    }

  }
}

$index: index(map-values($md-color-map), $color);
@if $index == null or $index < 1 {
  @error 'The given color "#{$color}" is not a valid material design color. Unable to get a swatch.';
  @return $color;
}

$current-color-name: nth(map-keys($md-color-map), $index);
$colors: (
  'red'
  'pink'
  'purple'
  'deep-purple'
  'indigo'
  'blue'
  'light-blue'
  'cyan'
  'teal'
  'green'
  'light-green'
  'lime'
  'yellow'
  'amber'
  'orange'
  'deep-orange'
  'brown'
  'grey'
  'blue-grey'
);
$color-name: null;
$index: 1;
@while $color-name == null and $index < length($colors) {
  $md-color: nth($colors, $index);
  @if str-index($current-color-name, $md-color) != null {
    $color-name: $md-color;
  }

  $index: $index + 1;
}

@return map-get($md-color-map, "md-#{$color-name}-#{if($accent, 'a-', '')}#{$swatch}");

.md-progress--linear {
  background: get-swatch($secondary-color, $swatch, true);
  height: $md-linear-progress-height;
  overflow: hidden;
  position: relative;
  width: 100%;
}

.md-progress--linear-active {
  background: $secondary-color;
}

.md-progress--linear-determinate {
  height: 100%;
  position: absolute;
  z-index: 1;
}

.md-progress--linear-indeterminate {
  &::before,
  &::after {
    background: inherit;
    bottom: 0;
    content: '';
    position: absolute;
    top: 0;
    will-change: left, right;
    z-index: 1;
  }

  &::before {
    animation-duration: $md-progress-duration;
    animation-iteration-count: infinite;
    animation-name: md-linear-indeterminate;
    animation-timing-function: $md-transition-standard;
  }

  &::after {
    animation-delay: $md-linear-progress-delay;
    animation-duration: $md-progress-duration;
    animation-iteration-count: infinite;
    animation-name: md-linear-indeterminate-short;
    animation-timing-function: $md-transition-acceleration;
  }
}

.md-progress--linear-query {
  &::before,
  &::after {
    animation-direction: reverse;
  }
}


@keyframes md-linear-indeterminate {
  0% {
    left: -35%;
    right: 100%;
  }

  60% {
    left: 100%;
    right: -90%;
  }

  100% {
    left: 100%;
    right: -90%;
  }
}

@keyframes md-linear-indeterminate-short {
  0% {
    left: -200%;
    right: 100%;
  }

  60% {
    left: 107%;
    right: -8%;
  }

  100% {
    left: 107%;
    right: -8%;
  }
}

@if $secondary-color != $md-secondary-color {
  .md-progress--linear-active {
    background: $secondary-color;
  }
}

@if $secondary-color != $md-secondary-color or $swatch != $md-linear-progress-swatch {
  .md-progress--linear {
    background: get-swatch($secondary-color, $swatch, true);
  }
}

$swatch: get-swatch($secondary-color, if($light-theme, 500, 200));

.md-switch-container {
  height: $md-switch-height;
}

.md-switch-track {
  border-radius: $md-switch-track-radius;
  flex-shrink: 0;
  height: $md-switch-track-height;
  margin-left: 12px;
  margin-right: 12px;
  position: relative;
  width: $md-switch-track-width;
}

.md-switch-track--on {
  background: rgba($swatch, .5);
}

.md-switch-track--off {
  background: if($light-theme, $md-switch-light-theme-track-off, $md-switch-dark-theme-track-off);
}

.md-switch-track--disabled {
  background: if($light-theme, $md-switch-light-theme-track-disabled, $md-switch-dark-theme-track-disabled);
}

.md-switch-thumb {
  @include md-box-shadow(1);

  border-radius: 50%;
  content: '';
  display: block;
  height: $md-switch-thumb-size;
  position: absolute;
  transition-duration: $md-transition-time;
  transition-property: background, transform;
  width: $md-switch-thumb-size;
}

.md-switch-thumb--on {
  background: $swatch;
  transform: translate3d(#{$md-switch-track-width - $md-switch-thumb-size + $md-switch-thumb-offset}, -$md-switch-thumb-offset, 0);
}

.md-switch-thumb--off {
  background: if($light-theme, $md-switch-light-theme-thumb-off, $md-switch-dark-theme-thumb-off);
  transform: translate3d(-$md-switch-thumb-offset, -$md-switch-thumb-offset, 0);
}

.md-switch-thumb--disabled {
  background: if($light-theme, $md-switch-light-theme-thumb-disabled, $md-switch-dark-theme-thumb-disabled);
}

@if $secondary-color != $md-secondary-color {
  $swatch: get-swatch($secondary-color, if($light-theme, 500, 200));

  .md-switch-track--on {
    background: rgba($swatch, .5);
  }

  .md-switch-thumb--on {
    background: $swatch;
  }
}

@if $light-theme != $md-light-theme {
  .md-switch-track--off {
    background: if($light-theme, $md-switch-light-theme-track-off, $md-switch-dark-theme-track-off);
  }

  .md-switch-track--disabled {
    background: if($light-theme, $md-switch-light-theme-track-disabled, $md-switch-dark-theme-track-disabled);
  }

  .md-switch-thumb--off {
    background: if($light-theme, $md-switch-light-theme-thumb-off, $md-switch-dark-theme-thumb-off);
  }

  .md-switch-thumb--disabled {
    background: if($light-theme, $md-switch-light-theme-thumb-disabled, $md-switch-dark-theme-thumb-disabled);
  }
}

.md-color--text {
  color: get-color('text', $light-theme);
}

.md-color--secondary-text {
  color: get-color('secondary', $light-theme);
}

.md-color--disabled {
  color: get-color('disabled', $light-theme);
}

.md-color--primary {
  color: $primary-color;
}

.md-color--primary-ink {
  .md-ink {
    background: rgba($primary-color, .26);
  }
}

.md-color--secondary {
  color: $secondary-color;
}

.md-color--secondary-ink {
  .md-ink {
    background: rgba($secondary-color, .26);
  }
}

.md-background {
  background: get-color('background', $light-theme);
}

.md-background--card {
  background: get-color('card', $light-theme);
}

.md-background--primary {
  background: $primary-color;
  color: if(lightness($primary-color) > 40, $md-white-base, $md-black-base);
}

.md-background--secondary {
  background: $secondary-color;
  color: if(lightness($secondary-color) > 40, $md-white-base, $md-black-base);
}

@if $include-media {
  @media #{$md-desktop-media} {
    .md-background--primary-hover:hover {
      background: rgba($primary-color, .9);
    }

    .md-background--secondary-hover:hover {
      background: rgba($secondary-color, .9);
    }
  }
}

@if $include-color-class-names {
  @include react-md-color-class-names;
}

