@if $primary-color != $md-primary-color {
  .md-color--primary {
    color: $primary-color;

  }

  .md-color--primary-ink {
    .md-ink {
      background: rgba($primary-color, .26);
    }
  }

  .md-background--primary {
    background: $primary-color;
  }
}

@if $secondary-color != $md-secondary-color {
  .md-color--secondary {
    color: $secondary-color;
  }

  .md-color--secondary-ink {
    .md-ink {
      background: rgba($secondary-color, .26);
    }
  }

  .md-background--secondary {
    background: $secondary-color;
  }
}

@if $light-theme != $md-light-theme {
  .md-color--text {
    color: get-color('text', $light-theme);
  }

  .md-color--secondary-text {
    color: get-color('secondary', $light-theme);
  }

  .md-color--disabled {
    color: get-color('disabled', $light-theme);
  }

  .md-background {
    background: get-color('background', $light-theme);
  }

  .md-background--card {
    background: get-color('card', $light-theme);
  }
}

@if $include-media and ($primary-color != $md-primary-color or $secondary-color != $md-secondary-color) {
  @media #{$md-desktop-media} {
    @if $primary-color != $md-primary-color {
      .md-background--primary-hover:hover {
        background: rgba($primary-color, .9);
      }
    }

    @if $secondary-color != $md-secondary-color {
      .md-background--secondary-hover:hover {
        background: rgba($secondary-color, .9);
      }
    }
  }
}

@if $html-class-name != null {
  @include react-md-theme-typography($html-class-name, $light-theme);

  .#{$html-class-name} {
    @include react-md-theme-avatars($light-theme);
    @include react-md-theme-buttons($primary-color, $secondary-color, $light-theme);
    @include react-md-theme-cards($light-theme);
    @include react-md-theme-chips($light-theme);
    @include react-md-theme-data-tables($light-theme);
    @include react-md-theme-dividers($light-theme);
    @include react-md-theme-expansion-panels($light-theme);
    @include react-md-theme-icons($light-theme);
    @include react-md-theme-inks($light-theme);
    @include react-md-theme-lists($light-theme);
    @include react-md-theme-pickers($primary-color, $light-theme);
    @include react-md-theme-progress($secondary-color, $progress-swatch);
    @include react-md-theme-selection-controls($secondary-color, $light-theme);
    @include react-md-theme-sliders($primary-color, $secondary-color, $light-theme);
    @include react-md-theme-tabs($secondary-color);
    @include react-md-theme-text-fields($primary-color, $light-theme, $error-color);
    @include react-md-theme-toolbars($light-theme);

    @include react-md-theme-colors($primary-color, $secondary-color, $light-theme);
  }
} @else {
  @include react-md-theme-avatars($light-theme);
  @include react-md-theme-buttons($primary-color, $secondary-color, $light-theme);
  @include react-md-theme-cards($light-theme);
  @include react-md-theme-chips($light-theme);
  @include react-md-theme-data-tables($light-theme);
  @include react-md-theme-dividers($light-theme);
  @include react-md-theme-expansion-panels($light-theme);
  @include react-md-theme-icons($light-theme);
  @include react-md-theme-inks($light-theme);
  @include react-md-theme-lists($light-theme);
  @include react-md-theme-pickers($primary-color, $light-theme);
  @include react-md-theme-progress($secondary-color, $progress-swatch);
  @include react-md-theme-selection-controls($secondary-color, $light-theme);
  @include react-md-theme-sliders($primary-color, $secondary-color, $light-theme);
  @include react-md-theme-tabs($secondary-color);
  @include react-md-theme-text-fields($primary-color, $light-theme, $error-color);
  @include react-md-theme-toolbars($light-theme);

  @include react-md-theme-colors($primary-color, $secondary-color, $light-theme);
}

