.md-expansion-panel-list {
  @extend %md-list-unstyled;
}

.md-expansion-panel {
  background: get-color('card', $light-theme);
  transition-duration: $md-transition-time;
  transition-property: margin;

  &--expanded {
    &:not(:first-child) {
      margin-top: $md-expansion-panel-opened-margin;
    }

    &:not(:last-child) {
      margin-bottom: $md-expansion-panel-opened-margin;
    }
  }
}

.md-panel-column:not(:last-child) {
  padding-right: $md-default-padding;
}

.md-panel-header {
  align-items: center;
  display: flex;
  font-size: $md-expansion-panel-font-size;
  height: $md-height;
  justify-content: space-between;
  padding-left: $md-expansion-panel-padding;
  padding-right: $md-expansion-panel-padding;
  transition-duration: $md-transition-time;
  transition-property: background, height;

  &--expanded {
    height: $md-expansion-panel-expanded-height;
  }

  &--focused {
    background: $md-grey-200;
  }
}

.md-panel-content {
  padding: 0 $md-expansion-panel-padding $md-default-padding;
}

.md-panel-secondary-label {
  color: get-color('secondary', $light-theme);
  font-size: $md-expansion-panel-secondary-font-size;
}

.md-selection-control-container {
  align-items: center;
  display: flex;
  height: $md-height;
}

.md-selection-control-container--inline {
  display: inline-flex;
}

.md-selection-control-input {
  height: 0;
  position: absolute;
  visibility: hidden;
  width: 0;
  z-index: -1;
}

.md-selection-control-label {
  cursor: inherit;
  display: inline-block;
}

@if $include-group {
  .md-selection-control-group {
    border: 0;
  }
}

@if $include-switches {
  @include react-md-selection-controls-switch($secondary-color, $light-theme);
}

height: initial;
min-height: $md-data-table-column-height;
padding-bottom: $md-default-padding;
padding-top: $md-default-padding;
vertical-align: top;

.md-table-column {
  padding-right: $md-data-table-padding;
  position: relative;
  white-space: nowrap;

  &:first-child {
    padding-left: $md-data-table-padding;
  }

  &--adjusted {
    padding-right: $md-data-table-min-padding;
  }

  &--header {
    font-size: $md-data-table-header-font-size;
    font-weight: $md-font-medium;
    height: $md-data-table-header-height;

    .md-icon {
      font-size: $md-data-table-header-icon-font-size;
    }
  }

  &--data {
    font-size: $md-data-table-content-font-size;
  }

  &--grow {
    width: 100%;
  }

  &--select-header {
    padding-left: $md-default-padding;
  }
}

.md-dialog-container.md-overlay {
  transition-duration: $md-dialog-transition-time;
}

.md-dialog {
  @include react-md-dialog-width;

  cursor: initial;
  position: absolute;
}

.md-dialog--centered {
  left: 50%;
  max-height: calc(100vh - #{$md-dialog-vertical-keyline * 2});
  max-width: calc(100vw - #{$md-dialog-horizontal-keyline * 2});
  top: 50%;
  transform: translate3d(-50%, -50%, 0);

  .md-list {
    padding-bottom: $md-dialog-padding - $md-default-padding;
    padding-top: 0;
  }

  .md-list-tile {
    height: initial;
    padding: $md-default-padding $md-dialog-padding;
  }

  .md-tile-text--primary {
    white-space: normal;
  }
}

.md-dialog--centered-enter {
  transform: translate3d(-50%, calc(-50% + #{$md-dialog-translate-distance}), 0);

  &.md-dialog--centered-enter-active {
    transform: translate3d(-50%, -50%, 0);
    transition-duration: $md-dialog-transition-time;
    transition-property: transform;
  }
}

.md-dialog--centered-leave {
  transform: translate3d(-50%, -50%, 0);

  &.md-dialog--centered-leave-active {
    transform: translate3d(-50%, calc(-50% + #{$md-dialog-translate-distance}), 0);
    transition-duration: $md-dialog-transition-time;
    transition-property: transform;
  }
}

.md-dialog--full-page {
  height: 100vh;
  left: 0;
  overflow-y: auto;
  position: fixed;
  top: 0;
  width: 100vw;
  z-index: $md-dialog-full-page-z-index;
}

.md-dialog--full-page-enter {
  transform: scale(0);

  &.md-dialog--full-page-enter-active {
    transform: scale(1);
    transition-duration: $md-dialog-transition-time;
    transition-property: transform;
  }
}

.md-dialog--full-page-leave {
  transform: scale(1);

  &.md-dialog--full-page-leave-active {
    transform: scale(0);
    transition-duration: $md-dialog-transition-time;
    transition-property: transform;
  }
}

@include react-md-dialogs-header;
@include react-md-dialogs-content;
@include react-md-dialogs-footer;

