  @include react-md-lists-media($include-icon, $include-avatar, $include-two-lines, $include-three-lines);
}

.md-list {
  @extend %md-list-unstyled;
  background: get-color('card', $light-theme);
  user-select: none;

  .md-avatar,
  .md-icon {
    flex-shrink: 0;
  }

  .md-divider {
    margin-bottom: 8px;
    margin-top: 8px;
  }
}

.md-list-tile {
  align-items: center;
  display: flex;
  padding-left: $md-default-padding;
  padding-right: $md-default-padding;
  text-decoration: none;
  transition-duration: $md-transition-time;
  transition-property: background;
}

.md-list-tile--active {
  background: get-color('hover', $light-theme);
}

.md-list-item--inset {
  padding-left: $md-list-left-padding;
}

.md-tile-content {
  flex-grow: 1;
}

@if $include-icon {
  .md-tile-content--left-icon {
    padding-left: $md-list-left-padding - $md-font-icon-size - $md-default-padding;
  }
}

@if $include-avatar {
  .md-tile-content--left-avatar {
    padding-left: $md-list-left-padding - $md-avatar-size - $md-default-padding;
  }
}

@if $include-icon or $include-avatar {
  .md-tile-addon--icon {
    height: $md-font-icon-size;
  }

  .md-color--primary .md-icon {
    color: inherit;
  }

  .md-tile-addon--avatar {
    height: $md-avatar-size;
  }

  .md-tile-content--right-padding {
    padding-right: $md-default-padding;
  }
}

%tile-text {
  display: block;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.md-tile-text--primary {
  @extend %tile-text;
}

@if $include-two-lines or $include-three-lines {
  .md-tile-text--secondary {
    @extend %tile-text;
  }
}

@if $include-three-lines {
  .md-tile-text--three-lines {
    // scss-lint:disable PropertySortOrder
    white-space: pre-line;

    // Magic with webkit-line clamp
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 2;
  }

  @if $include-icon or $include-avatar {
    .md-list-tile--three-lines .md-tile-addon {
      align-self: flex-start;
    }
  }
}

@if $nested-items-depth != null and $nested-items-depth > 0 {
  @include react-md-lists-nested($nested-items-depth, $nested-items-increment);
}

@if $include-controls {
  .md-list-control {
    flex-grow: 1;

    .md-selection-control-label {
      width: 100%;
    }
  }

  .md-tile-content--left-button {
    padding-left: $md-list-left-padding - $md-font-icon-size * 2;
  }

  .md-list-control--right {
    justify-content: flex-end;
  }

  .md-list-tile--control-left {
    padding-left: 0;
  }

  .md-list-tile--control-right {
    padding-right: 0;
  }
}

@if $include-media {
  @include react-md-navigation-drawers-media($enforce-height);
}

.md-toolbar {
  &.md-toolbar--over-drawer {
    z-index: $md-drawer-z-index + 2;
  }
}

.md-title--drawer-active.md-title--drawer-active {
  transition-duration: $md-drawer-transition-time;
  transition-property: margin-left;
}

.md-navigation-drawer-content {
  @if $md-navigation-drawer-enforce-height {
    overflow-y: auto;
  }

  &--inactive {
    margin-left: 0;
  }

  &--active {
    transition-duration: $md-drawer-transition-time;
    transition-property: margin-left;
  }

  &--prominent-offset {
    $height: calc(100vh - #{$md-toolbar-mobile-prominent-height});

    @if $md-navigation-drawer-enforce-height {
      height: $height;
    } @else {
      min-height: $height;
    }
  }
}

.md-title.md-title--permanent-offset {
  margin-left: $md-drawer-tablet-width + $md-default-padding;
}

.md-drawer .md-title--toolbar-offset {
  margin-left: $md-default-padding;
}

@if $include-cross-fade {
  .md-cross-fade-enter {
    opacity: .01;
    transform: translate3d(0, $md-cross-fade-distance, 0);

    &.md-cross-fade-enter-active {
      opacity: 1;
      transform: translate3d(0, 0, 0);
      transition-duration: $md-cross-fade-transition-time;
      transition-property: transform, opacity;
    }
  }
}

.md-picker-container {
  position: relative;
}

.md-picker-content-container {
  background: if($light-theme, get-color('card', true), get-color('background', false));
}

.md-picker--inline {
  @include md-box-shadow(3);

  position: absolute;
  z-index: $md-picker-inline-z-index;
}

.md-picker--inline-icon {
  left: $md-font-icon-size + $md-default-padding;
}

.md-picker-control {
  @extend %md-text-left;

  padding: 0;
}

.md-picker-text {
  color: get-color('secondary', false);

  &.md-picker-text > * {
    color: inherit;
  }

  > * {
    font-weight: inherit;
    margin: 0;
    transition-duration: $md-transition-time;
    transition-property: color, font;
  }
}

.md-picker-text--active {
  color: get-color('text', false);
  font-weight: $md-font-medium;
}

.md-dialog--picker {
  max-height: calc(100vh - #{$md-picker-mobile-margin});
  max-width: calc(100vw - #{$md-picker-mobile-margin});
  width: auto;
}

.md-dialog-content--picker {
  max-height: inherit;
  max-width: inherit;
  padding: 0;
}

.md-picker-header {
  background: if($light-theme, $primary-color, get-color('card', false));
  padding: $md-dialog-padding;
}

@if $include-forceful-classes {
  @include react-md-pickers-force-orientation;
}

@if $include-media {
  @include react-md-slider-indicators-media;
}

.md-slider-container {
  .md-icon {
    $margin: ($md-slider-height - $md-font-icon-size) / 2;

    margin-bottom: $margin;
    margin-top: $margin;
  }
}

.md-slider-ind {
  display: block;
  text-align: center;
}

.md-slider-track--ind-left {
  margin-left: $md-default-padding;
}

.md-slider-track--ind-right {
  margin-right: $md-default-padding;
}

.md-subheader {
  font-size: $md-subheader-font-size;
  font-weight: $md-font-medium;
  height: $md-subheader-height;
  line-height: $md-subheader-height;
  padding-left: $md-default-padding;
  padding-right: $md-default-padding;
}

.md-text-field-message-container {
  color: get-color('hint', $light-theme);
  display: flex;
  font-size: $md-text-field-message-font-size;
  justify-content: space-between;
}

.md-text-field-message-container--error {
  color: $error-color;
}

.md-text-field-message-container--count-only {
  justify-content: flex-end;
}

@if $include-icon {
  .md-text-field-message-container--left-icon-offset {
    margin-left: 40px;
  }

  .md-text-field-message-container--right-icon-offset {
    margin-right: 40px;
  }
}

.md-text-field-message {
  flex-grow: 1;
  transition-duration: $md-transition-time;
  transition-property: opacity;
}

.md-text-field-message--counter {
  display: block;
  flex-grow: initial;
  flex-shrink: 0;
  padding-left: $md-default-padding;
}

.md-text-field-message--inactive {
  opacity: 0;
}

.md-text-field-message--active {
  opacity: 1;
}
