@if $include-media {
  @include react-md-drawers-media;
}

.md-drawer {
  transition-duration: $md-drawer-transition-time;
  transition-property: transform;

  &--fixed {
    height: 100vh;
    position: fixed;
    top: 0;
    z-index: $md-drawer-z-index;
  }

  &--inline {
    display: inline-block;
    height: 100%;
  }

  &--left {
    left: 0;
  }

  &--right {
    right: 0;
  }

  &--active {
    transform: translate3d(0, 0, 0);
  }
}

.md-list--drawer {
  height: 100vh;
  overflow-y: auto;
}

.md-overlay--drawer.md-overlay--drawer {
  transition-duration: $md-drawer-transition-time;
}

@if $include-media {
  @include react-md-navigation-drawers-media($enforce-height);
}

.md-toolbar {
  &.md-toolbar--over-drawer {
    z-index: $md-drawer-z-index + 2;
  }
}

.md-title--drawer-active.md-title--drawer-active {
  transition-duration: $md-drawer-transition-time;
  transition-property: margin-left;
}

.md-navigation-drawer-content {
  @if $md-navigation-drawer-enforce-height {
    overflow-y: auto;
  }

  &--inactive {
    margin-left: 0;
  }

  &--active {
    transition-duration: $md-drawer-transition-time;
    transition-property: margin-left;
  }

  &--prominent-offset {
    $height: calc(100vh - #{$md-toolbar-mobile-prominent-height});

    @if $md-navigation-drawer-enforce-height {
      height: $height;
    } @else {
      min-height: $height;
    }
  }
}

