.md-ink-container {
  border-radius: inherit;
  height: 100%;
  left: 0;
  overflow: hidden;
  pointer-events: none;
  position: absolute;
  top: 0;
  width: 100%;
  z-index: 1;
}

.md-ink-container--2x {
  height: 200%;
  left: -50%;
  top: -50%;
  width: 200%;
}

.md-ink {
  background: get-color('divider', $light-theme);
  border-radius: 50%;
  display: block;
  opacity: 1;
  position: absolute;
  transform: scale(0);
  z-index: -1;
}

.md-ink--active {
  transition-duration: $md-ink-enter-transition-time;
  transition-property: opacity, transform;
  transition-timing-function: $md-transition-acceleration;
}

.md-ink--expanded {
  transform: scale(.9);
}

.md-ink--pulsing {
  animation-duration: $md-ink-pulse-time;
  animation-iteration-count: infinite;
  animation-name: md-ink-pulse;
  animation-timing-function: $md-transition-standard;
}

.md-ink--leaving {
  opacity: 0;
  transition-duration: .3s;
  transition-timing-function: $md-transition-deceleration;
}

@keyframes md-ink-pulse {
  0% {
    transform: scale(.9);
  }

  66% {
    transform: scale(.85);
  }

  100% {
    transform: scale(.9);
  }
}

