@if $include-media {
  @include react-md-buttons-media($include-flat, $include-raised);
}

// scss-lint:disable QualifyingElement
a.md-btn {
  text-decoration: none;
}

.md-btn {
  background: transparent;
  border: 0;
  border-radius: 2px;
  position: relative;
  transition-duration: $md-transition-time;
  transition-property: background, color;

  &:focus {
    outline: none;
  }

  .md-icon {
    color: inherit;
  }

  .md-icon-separator {
    height: 100%;
  }
}

.md-btn--hover {
  background: if($light-theme, $md-btn-light-theme-hover-color, $md-btn-dark-theme-hover-color);
}

.md-btn--color-primary-active {
  background: rgba($primary-color, .12);
}

.md-btn--color-secondary-active {
  background: rgba($secondary-color, .12);
}

@if $include-flat or $include-raised {
  .md-btn--text {
    font-weight: $md-font-medium;
    min-width: $md-btn-min-width;
    padding: 8px $md-btn-lr-padding;
    text-transform: uppercase;
    transition-duration: $md-transition-time;
  }
}

@if $include-raised or $include-floating {
  .md-btn--raised {
    @include md-box-shadow(1);

    transition: background $md-transition-time, box-shadow $md-transition-time * 2, color $md-transition-time;
  }

  .md-btn--raised-disabled {
    background: get-color('divider', $light-theme);
  }

  .md-btn--raised-pressed {
    @include md-box-shadow(2);
  }
}

@if $include-icon or $include-floating {
  .md-btn--icon {
    border-radius: 50%;
    color: get-color('icon', $light-theme);
    height: $md-btn-icon-size;
    padding: $md-btn-icon-padding;
    width: $md-btn-icon-size;
  }
}

@if $include-floating {
  .md-btn--floating {
    @include md-box-shadow(2);

    height: $md-btn-floating-size;
    padding: ($md-btn-floating-size - $md-font-icon-size) / 2;
    transition-property: background, box-shadow, color;
    width: $md-btn-floating-size;
  }

  .md-btn--floating-pressed {
    @include md-box-shadow(4);
  }

  .md-btn--floating-mini {
    height: $md-btn-floating-mini-size;
    padding: ($md-btn-floating-mini-size - $md-font-icon-size) / 2;
    width: $md-btn-floating-mini-size;
  }

  .md-btn--fixed {
    position: fixed;
    z-index: $md-btn-fixed-z-index;
  }
}

.md-chip {
  align-items: center;
  background: if($light-theme, $md-chip-light-theme-bg-color, $md-chip-dark-theme-bg-color);
  border: 0;
  border-radius: $md-chip-height / 2;
  display: inline-flex;
  height: $md-chip-height;
  padding-left: $md-chip-default-padding;
  padding-right: $md-chip-default-padding;
  position: relative;
  transition-duration: $md-transition-time;
  transition-property: box-shadow, background;
  vertical-align: top;
  white-space: nowrap;

  &:focus {
    @include md-box-shadow(1);
    outline: none;
  }
}

.md-chip--hover.md-chip--hover {
  background: $md-grey-700;
}

.md-chip-text {
  color: if($light-theme, $md-chip-light-theme-color, $md-chip-dark-theme-color);
  font-size: $md-chip-font-size;
  transition-duration: $md-transition-time;
  transition-property: color;
}

@if $include-avatar or $include-contact {
  @include react-md-chips-avatar;
}

@if $include-remove {
  @include react-md-chips-remove;
}

@if $include-contact {
  @include react-md-chips-contact;
}

// has to go last so it has higher precidence.
.md-chip-text--hover {
  color: $md-white-base;
}

.md-edit-dialog-column {
  padding-right: 0;
}

.md-edit-dialog {
  left: 0;
  min-width: $md-edit-dialog-min-width;
  padding-right: $md-data-table-padding;
  top: 0;
  transition-duration: .3s;
  transition-property: background, box-shadow, padding;

  &--active {
    @include md-box-shadow(1);

    padding: $md-data-table-padding;
    padding-bottom: 0;
    z-index: 2;
  }

  .md-title {
    margin: 0;
  }

  .md-dialog-footer {
    margin-right: -$md-data-table-padding;
  }

  .md-text-field-container {
    padding: 0;
  }

  .md-text-field-icon-container {
    align-items: center;
  }
}

@if $include-media {
  @include react-md-menus-media;
}

.md-menu-container {
  position: relative;
}

.md-menu-container--menu-below {
  .md-menu-enter {
    transform: scaleY(0);

    &.md-menu-enter-active {
      transform: scaleY(1);
    }
  }
}

.md-list--menu {
  @include md-box-shadow(2);

  position: absolute;
  top: 0;
  z-index: $md-menu-z-index;
}

.md-list--menu-contained {
  width: 100%;
}

.md-list--menu-scrollable {
  overflow-y: auto;
}

.md-list--menu-tr {
  right: 0;
  transform-origin: 100% 0;
}

.md-list--menu-tl {
  transform-origin: 0 0;
}

.md-list--menu-br {
  right: 0;
  transform-origin: 100% 100%;
}

.md-list--menu-bl {
  transform-origin: 0 100%;
}

.md-list--menu-context {
  transform-origin: 50% 0;
}

.md-list--menu-nested {
  @include md-box-shadow(3);

  position: absolute;
  z-index: $md-menu-z-index + 1;
}

.md-list--menu-below {
  left: 0;
  top: 100%;
  transform-origin: 50% 0;
  width: 100%;
}

.md-menu-enter {
  transform: scale(0);
}

.md-menu-leave {
  opacity: 1;
}

.md-menu-enter-active,
.md-menu-leave-active {
  transition-duration: $md-menu-transition-time;
}

.md-menu-enter.md-menu-enter-active {
  transform: scale(1);
  transition-property: transform;
}

.md-menu-leave.md-menu-leave-active {
  opacity: 0;
  transition-property: opacity;
}

.md-menu-enter.md-menu-enter-active {
  transition-timing-function: $md-transition-deceleration;
}

.md-menu-leave.md-menu-enter-leave {
  transition-timing-function: $md-transition-acceleration;
}

@if $include-cascading {
  @include react-md-menus-cascading;
}

