.md-picker--date {
  .md-picker-control {
    display: block;
  }
}

.md-picker-content--calendar {
  padding-left: 12px;
  padding-right: 12px;
}

.md-calendar-controls {
  align-items: center;
  display: flex;

  .md-title {
    flex-grow: 1;
    font-size: 16px;
    margin-bottom: 0;
    text-align: center;
  }
}

