@if $media-included {
  @include react-md-tooltips-media;
}

.md-tooltip-container {
  height: 100%;
  left: 0;
  pointer-events: none;
  position: absolute;
  top: 0;
  user-select: none;
  width: 100%;
  z-index: 1;
}

.md-tooltip {
  align-items: center;
  background: $md-tooltip-color;
  color: $md-white-base;
  display: inline-flex;
  font-weight: $md-font-medium;
  opacity: $md-tooltip-opacity;
  outline: none;
  position: absolute;
  white-space: nowrap;
}

.md-tooltip--active {
  transition-duration: $md-transition-time;
  transition-property: opacity, transform;
}

.md-tooltip--enter,
.md-tooltip--leave-active {
  opacity: 0;
}

.md-tooltip--enter-active {
  opacity: $md-tooltip-opacity;
  transition-timing-function: $md-transition-deceleration;
}

.md-tooltip--leave-active {
  transition-timing-function: $md-transition-acceleration;
}

.md-tooltip--horizontal {
  left: 50%;
  transform: translate3d(-50%, 0, 0);
}

.md-tooltip--vertical {
  top: 50%;
  transform: translate3d(0, -50%, 0);
}

.md-tooltip--top-active {
  transform: translate3d(-50%, -100%, 0);
}

.md-tooltip--right-active {
  transform: translate3d(100%, -50%, 0);
}

.md-tooltip--bottom-active {
  transform: translate3d(-50%, 100%, 0);
}

.md-tooltip--left-active {
  transform: translate3d(-100%, -50%, 0);
}

