context.code:
.md-bottom-nav {
  display: block;
  flex-grow: 1;
  font-size: $md-bottom-navigation-inactive-font-size;
  height: $md-bottom-navigation-height;
  max-width: $md-bottom-navigation-max-width;
  padding: $md-bottom-navigation-top-padding-inactive $md-bottom-navigation-lr-padding $md-bottom-navigation-bottom-padding;
  text-align: center;
  text-decoration: none;
  user-select: none;

  &--active {
    flex-shrink: 0;
    font-size: $md-bottom-navigation-active-font-size;
    padding-top: $md-bottom-navigation-top-padding-active;
  }

  &--fixed {
    min-width: $md-bottom-navigation-fixed-min-width;
  }

  &--shifting {
    min-width: $md-bottom-navigation-shifting-active-min-width;
    position: initial;
    transition-duration: $md-transition-time;
    transition-property: max-width;

    &-inactive {
      max-width: $md-bottom-navigation-shifting-inactive-max-width;
      min-width: $md-bottom-navigation-shifting-inactive-min-width;
      padding-top: 16px;
    }

    .md-ink-container {
      overflow: visible;
    }

    .md-ink {
      background: $md-bottom-navigation-shifting-ink-color;
    }
  }

  &-label {
    transition-duration: $md-transition-time;
    transition-property: color, font-size;

    &--shifting-inactive {
      max-width: $md-bottom-navigation-shifting-inactive-min-width - $md-bottom-navigation-lr-padding * 2;
      overflow: hidden;
      white-space: nowrap;
    }
  }

  .md-icon {
    @extend %md-block-centered;
  }

  // scss-lint:disable QualifyingElement
  // don't know why links render incorrectly
  a.md-bottom-nav {
    .md-icon {
      margin-top: ($md-bottom-navigation-height - $md-font-icon-size - 16px) / 2;
    }
  }
}

@if $include-media {
  @include react-md-buttons-media($include-flat, $include-raised);
}

// scss-lint:disable QualifyingElement
a.md-btn {
  text-decoration: none;
}

.md-btn {
  background: transparent;
  border: 0;
  border-radius: 2px;
  position: relative;
  transition-duration: $md-transition-time;
  transition-property: background, color;

  &:focus {
    outline: none;
  }

  .md-icon {
    color: inherit;
  }

  .md-icon-separator {
    height: 100%;
  }
}

.md-btn--hover {
  background: if($light-theme, $md-btn-light-theme-hover-color, $md-btn-dark-theme-hover-color);
}

.md-btn--color-primary-active {
  background: rgba($primary-color, .12);
}

.md-btn--color-secondary-active {
  background: rgba($secondary-color, .12);
}

@if $include-flat or $include-raised {
  .md-btn--text {
    font-weight: $md-font-medium;
    min-width: $md-btn-min-width;
    padding: 8px $md-btn-lr-padding;
    text-transform: uppercase;
    transition-duration: $md-transition-time;
  }
}

@if $include-raised or $include-floating {
  .md-btn--raised {
    @include md-box-shadow(1);

    transition: background $md-transition-time, box-shadow $md-transition-time * 2, color $md-transition-time;
  }

  .md-btn--raised-disabled {
    background: get-color('divider', $light-theme);
  }

  .md-btn--raised-pressed {
    @include md-box-shadow(2);
  }
}

@if $include-icon or $include-floating {
  .md-btn--icon {
    border-radius: 50%;
    color: get-color('icon', $light-theme);
    height: $md-btn-icon-size;
    padding: $md-btn-icon-padding;
    width: $md-btn-icon-size;
  }
}

@if $include-floating {
  .md-btn--floating {
    @include md-box-shadow(2);

    height: $md-btn-floating-size;
    padding: ($md-btn-floating-size - $md-font-icon-size) / 2;
    transition-property: background, box-shadow, color;
    width: $md-btn-floating-size;
  }

  .md-btn--floating-pressed {
    @include md-box-shadow(4);
  }

  .md-btn--floating-mini {
    height: $md-btn-floating-mini-size;
    padding: ($md-btn-floating-mini-size - $md-font-icon-size) / 2;
    width: $md-btn-floating-mini-size;
  }

  .md-btn--fixed {
    position: fixed;
    z-index: $md-btn-fixed-z-index;
  }
}

.md-chip {
  align-items: center;
  background: if($light-theme, $md-chip-light-theme-bg-color, $md-chip-dark-theme-bg-color);
  border: 0;
  border-radius: $md-chip-height / 2;
  display: inline-flex;
  height: $md-chip-height;
  padding-left: $md-chip-default-padding;
  padding-right: $md-chip-default-padding;
  position: relative;
  transition-duration: $md-transition-time;
  transition-property: box-shadow, background;
  vertical-align: top;
  white-space: nowrap;

  &:focus {
    @include md-box-shadow(1);
    outline: none;
  }
}

.md-chip--hover.md-chip--hover {
  background: $md-grey-700;
}

.md-chip-text {
  color: if($light-theme, $md-chip-light-theme-color, $md-chip-dark-theme-color);
  font-size: $md-chip-font-size;
  transition-duration: $md-transition-time;
  transition-property: color;
}

@if $include-avatar or $include-contact {
  @include react-md-chips-avatar;
}

@if $include-remove {
  @include react-md-chips-remove;
}

@if $include-contact {
  @include react-md-chips-contact;
}

// has to go last so it has higher precidence.
.md-chip-text--hover {
  color: $md-white-base;
}

thead .md-table-row,
tbody .md-table-row:not(:last-child) {
  border-bottom: 1px solid get-color('divider', $light-theme);
}

tbody .md-table-row {
  transition-duration: $md-transition-time;
  transition-property: background;

  &--active {
    background: if($light-theme, $md-data-table-selected-color, $md-data-table-dark-theme-selected-color);
  }

  &--hover {
    background: if($light-theme, $md-data-table-hover-color, $md-data-table-dark-theme-hover-color);
  }
}

.md-expansion-panel-list {
  @extend %md-list-unstyled;
}

.md-expansion-panel {
  background: get-color('card', $light-theme);
  transition-duration: $md-transition-time;
  transition-property: margin;

  &--expanded {
    &:not(:first-child) {
      margin-top: $md-expansion-panel-opened-margin;
    }

    &:not(:last-child) {
      margin-bottom: $md-expansion-panel-opened-margin;
    }
  }
}

.md-panel-column:not(:last-child) {
  padding-right: $md-default-padding;
}

.md-panel-header {
  align-items: center;
  display: flex;
  font-size: $md-expansion-panel-font-size;
  height: $md-height;
  justify-content: space-between;
  padding-left: $md-expansion-panel-padding;
  padding-right: $md-expansion-panel-padding;
  transition-duration: $md-transition-time;
  transition-property: background, height;

  &--expanded {
    height: $md-expansion-panel-expanded-height;
  }

  &--focused {
    background: $md-grey-200;
  }
}

.md-panel-content {
  padding: 0 $md-expansion-panel-padding $md-default-padding;
}

.md-panel-secondary-label {
  color: get-color('secondary', $light-theme);
  font-size: $md-expansion-panel-secondary-font-size;
}

$md-icon-color: if($light-theme, $md-light-theme-secondary-text-color, $md-dark-theme-secondary-text-color);

.md-icon {
  color: $md-icon-color;
  font-size: $md-font-icon-size;
  text-align: center;
  transition-duration: $md-transition-time;
  transition-property: color;
  user-select: none;

  &--disabled {
    color: if($light-theme, $md-font-icon-light-theme-disabled-color, $md-font-icon-dark-theme-disabled-color);
  }

  &--inherit {
    color: inherit;
  }
}

@if $include-separators {
  @include react-md-icon-separators;
}

@if $include-media {
  @include react-md-lists-media($include-icon, $include-avatar, $include-two-lines, $include-three-lines);
}

.md-list {
  @extend %md-list-unstyled;
  background: get-color('card', $light-theme);
  user-select: none;

  .md-avatar,
  .md-icon {
    flex-shrink: 0;
  }

  .md-divider {
    margin-bottom: 8px;
    margin-top: 8px;
  }
}

.md-list-tile {
  align-items: center;
  display: flex;
  padding-left: $md-default-padding;
  padding-right: $md-default-padding;
  text-decoration: none;
  transition-duration: $md-transition-time;
  transition-property: background;
}

.md-list-tile--active {
  background: get-color('hover', $light-theme);
}

.md-list-item--inset {
  padding-left: $md-list-left-padding;
}

.md-tile-content {
  flex-grow: 1;
}

@if $include-icon {
  .md-tile-content--left-icon {
    padding-left: $md-list-left-padding - $md-font-icon-size - $md-default-padding;
  }
}

@if $include-avatar {
  .md-tile-content--left-avatar {
    padding-left: $md-list-left-padding - $md-avatar-size - $md-default-padding;
  }
}

@if $include-icon or $include-avatar {
  .md-tile-addon--icon {
    height: $md-font-icon-size;
  }

  .md-color--primary .md-icon {
    color: inherit;
  }

  .md-tile-addon--avatar {
    height: $md-avatar-size;
  }

  .md-tile-content--right-padding {
    padding-right: $md-default-padding;
  }
}

%tile-text {
  display: block;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.md-tile-text--primary {
  @extend %tile-text;
}

@if $include-two-lines or $include-three-lines {
  .md-tile-text--secondary {
    @extend %tile-text;
  }
}

@if $include-three-lines {
  .md-tile-text--three-lines {
    // scss-lint:disable PropertySortOrder
    white-space: pre-line;

    // Magic with webkit-line clamp
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 2;
  }

  @if $include-icon or $include-avatar {
    .md-list-tile--three-lines .md-tile-addon {
      align-self: flex-start;
    }
  }
}

@if $nested-items-depth != null and $nested-items-depth > 0 {
  @include react-md-lists-nested($nested-items-depth, $nested-items-increment);
}

@if $include-controls {
  .md-list-control {
    flex-grow: 1;

    .md-selection-control-label {
      width: 100%;
    }
  }

  .md-tile-content--left-button {
    padding-left: $md-list-left-padding - $md-font-icon-size * 2;
  }

  .md-list-control--right {
    justify-content: flex-end;
  }

  .md-list-tile--control-left {
    padding-left: 0;
  }

  .md-list-tile--control-right {
    padding-right: 0;
  }
}

.md-picker-container {
  position: relative;
}

.md-picker-content-container {
  background: if($light-theme, get-color('card', true), get-color('background', false));
}

.md-picker--inline {
  @include md-box-shadow(3);

  position: absolute;
  z-index: $md-picker-inline-z-index;
}

.md-picker--inline-icon {
  left: $md-font-icon-size + $md-default-padding;
}

.md-picker-control {
  @extend %md-text-left;

  padding: 0;
}

.md-picker-text {
  color: get-color('secondary', false);

  &.md-picker-text > * {
    color: inherit;
  }

  > * {
    font-weight: inherit;
    margin: 0;
    transition-duration: $md-transition-time;
    transition-property: color, font;
  }
}

.md-picker-text--active {
  color: get-color('text', false);
  font-weight: $md-font-medium;
}

.md-dialog--picker {
  max-height: calc(100vh - #{$md-picker-mobile-margin});
  max-width: calc(100vw - #{$md-picker-mobile-margin});
  width: auto;
}

.md-dialog-content--picker {
  max-height: inherit;
  max-width: inherit;
  padding: 0;
}

.md-picker-header {
  background: if($light-theme, $primary-color, get-color('card', false));
  padding: $md-dialog-padding;
}

@if $include-forceful-classes {
  @include react-md-pickers-force-orientation;
}

.md-picker--date {
  .md-picker-control {
    display: block;
  }
}

.md-picker-content--calendar {
  padding-left: 12px;
  padding-right: 12px;
}

.md-calendar-controls {
  align-items: center;
  display: flex;

  .md-title {
    flex-grow: 1;
    font-size: 16px;
    margin-bottom: 0;
    text-align: center;
  }
}

.md-calendar-date {
  @extend %md-inline-block;
  @extend %md-text-center;

  @if not $light-theme {
    color: get-color('secondary', false);

    .md-picker-text--active {
      color: get-color('background', false);
    }
  }

  margin: 0;
  width: calc(100% / 7);
}

.md-picker-content--year {
  overflow-y: auto;
}

.md-years {
  list-style: none;
  margin: 0;
  padding: 0;
}

.md-year {
  font-size: $md-year-font-size;
  padding: 12px;
  transition-duration: $md-transition-time;
  transition-property: color, font-size;

  &:focus:not(.md-year--active) {
    font-size: $md-year-focus-font-size;
  }

  &--active {
    font-size: $md-year-active-font-size;
    font-weight: $md-font-medium;
  }
}

.md-calendar-date--btn {

  &::after {
    background: $primary-color;
    border-radius: 50%;
    content: '';
    display: block;
    left: 50%;
    position: absolute;
    top: 50%;
    transform: translateX(-50%) translateY(-50%) scale(0);
    transition-duration: $md-transition-time;
    transition-property: transform;
    z-index: 0;
  }
}

.md-calendar-date--today {
  color: $primary-color;
  font-weight: $md-font-bold;
}

.md-calendar-date--btn-active {
  font-weight: $md-font-bold;

  &::after {
    transform: translateX(-50%) translateY(-50%) scale(1);
  }
}

.md-calendar-date--date {
  position: relative;
  z-index: 1;
}

.md-calendar-dow {
  font-size: $md-calendar-date-font-size;
  font-weight: $md-font-bold;
  line-height: $md-line-height;
}

.md-picker-content--clock {
  padding: $md-dialog-padding;
  padding-bottom: $md-dialog-padding / 2;
}

.md-time-periods .md-picker-control {
  display: block;
}

.md-time-period {
  font-size: 16em / $md-font-size-base;
  font-weight: $md-font-medium;
  margin: 0;
  padding: 0;
}

.md-clock-face {
  background: if($light-theme, $md-grey-100, get-color('card', false));
  border-radius: 50%;
  position: relative;
}

.md-clock-hand {
  height: 2px;
  position: absolute;
  transform-origin: 0 0;
  transition-duration: $md-transition-time;
  transition-property: width;
  z-index: 1;

  &--active {
    transition-property: transform, width;
  }

  &::before,
  &::after {
    background: $primary-color;
    border-radius: 50%;
    content: '';
    position: absolute;
  }

  &::before {
    height: $md-clock-hand-ball-size;
    left: -$md-clock-hand-ball-size / 2;
    // no idea why
    top: -2px;
    width: $md-clock-hand-ball-size;
  }

  &::after {
    height: $md-clock-time-size;
    right: -$md-clock-time-size / 2 + $md-clock-padding / 2;
    top: -$md-clock-time-size / 2;
    transition-duration: $md-transition-time;
    transition-property: background, border, transform;
    width: $md-clock-time-size;
  }

  &--minute-hover::after {
    transform: scale($md-clock-hand-ball-scale);
  }
}

.md-clock-time {
  align-items: center;
  display: flex;
  height: $md-clock-time-size;
  justify-content: center;
  position: absolute;
  width: $md-clock-time-size;
  z-index: 8;
}

.md-progress--circular-determinate {
  transition-duration: $md-transition-time;
  transition-property: transform;
}

.md-progress--circular-indeterminate {
  animation-duration: $md-progress-duration;
  animation-iteration-count: infinite;
  animation-name: md-circular-progress;
  animation-timing-function: linear;
}

.md-circular-progress-path {
  fill: none;
  stroke: $secondary-color;
  stroke-dasharray: $md-circular-progress-stroke-dasharray;
}

.md-circular-progress-path--animated {
  animation-duration: $md-progress-duration;
  animation-iteration-count: infinite;
  animation-name: md-circular-progress-dash;
  animation-timing-function: ease-ine-out;
  stroke-dashoffset: 0;
}

@keyframes md-circular-progress {
  0% { transform: rotate(0deg); }

  50% { transform: rotate(135deg); }

  75% { transform: rotate(450deg); }

  100% { transform: rotate(720deg); }
}

@keyframes md-circular-progress-dash {
  0% {
    stroke-dashoffset: $md-circular-progress-stroke-dasharray;
  }

  50% {
    stroke-dashoffset: $md-circular-progress-stroke-dasharray / 4;
  }

  100% {
    stroke-dashoffset: $md-circular-progress-stroke-dasharray;
  }
}

.md-select-field {
  cursor: inherit;
  user-select: none;
}

.md-select-field-container--below {
  padding-left: $md-select-field-left-padding;
  padding-right: $md-font-icon-size + $md-select-field-right-padding;
  transition-duration: $md-transition-time;
  transition-property: box-shadow;

  .md-text-field-inline-indicator {
    right: $md-select-field-right-padding;
  }
}

.md-drop-enter {
  transform: translate3d(0, $md-select-field-drop-distance, 0);

  &.md-drop-enter-active {
    transform: translate3d(0, 0, 0);
    transition-duration: $md-select-field-drop-transition-time;
    transition-property: transform;
  }
}

$swatch: get-swatch($secondary-color, if($light-theme, 500, 200));

.md-switch-container {
  height: $md-switch-height;
}

.md-switch-track {
  border-radius: $md-switch-track-radius;
  flex-shrink: 0;
  height: $md-switch-track-height;
  margin-left: 12px;
  margin-right: 12px;
  position: relative;
  width: $md-switch-track-width;
}

.md-switch-track--on {
  background: rgba($swatch, .5);
}

.md-switch-track--off {
  background: if($light-theme, $md-switch-light-theme-track-off, $md-switch-dark-theme-track-off);
}

.md-switch-track--disabled {
  background: if($light-theme, $md-switch-light-theme-track-disabled, $md-switch-dark-theme-track-disabled);
}

.md-switch-thumb {
  @include md-box-shadow(1);

  border-radius: 50%;
  content: '';
  display: block;
  height: $md-switch-thumb-size;
  position: absolute;
  transition-duration: $md-transition-time;
  transition-property: background, transform;
  width: $md-switch-thumb-size;
}

.md-switch-thumb--on {
  background: $swatch;
  transform: translate3d(#{$md-switch-track-width - $md-switch-thumb-size + $md-switch-thumb-offset}, -$md-switch-thumb-offset, 0);
}

.md-switch-thumb--off {
  background: if($light-theme, $md-switch-light-theme-thumb-off, $md-switch-dark-theme-thumb-off);
  transform: translate3d(-$md-switch-thumb-offset, -$md-switch-thumb-offset, 0);
}

.md-switch-thumb--disabled {
  background: if($light-theme, $md-switch-light-theme-thumb-disabled, $md-switch-dark-theme-thumb-disabled);
}

$invalid-color: validate-slider-primary-color($primary-color, $light-theme);

$md-slider-on-color: $primary-color;
$md-slider-off-color: if($light-theme, $md-slider-light-theme-off-color, $md-slider-dark-theme-off-color);
$bg-color: if($light-theme, $md-white-base, $md-dark-theme-background-color);
$md-slider-mask-ink-color: rgba($primary-color, $md-slider-mask-ink-opacity);

.md-slider-container {
  display: flex;
  flex-wrap: wrap;
}

.md-slider-label {
  display: block;
  width: 100%;
}

.md-slider-input {
  height: 0;
  position: absolute;
  visibility: hidden;
  width: 0;
  z-index: -1;
}

.md-slider-track {
  $center: ($md-slider-height / 2) - ($md-slider-track-height / 2);
  background: $md-slider-off-color;
  content: '';
  display: block;
  flex-grow: 1;
  flex-shrink: 0;
  height: $md-slider-track-height;
  margin-bottom: $center;
  margin-top: $center;
  position: relative;
}

.md-slider-track-fill {
  background: $md-slider-on-color;
  height: $md-slider-track-height;
  left: 0;
  margin: 0;
  position: absolute;
  top: 0;
  transition-duration: $md-transition-time;
  transition-property: width;
}

.md-slider-track-fill--dragging {
  transition-property: none;
}

.md-slider-thumb {
  border-radius: 50%;
  content: '';
  display: inline-block;
  height: $md-slider-thumb-size;
  position: absolute;
  top: -(($md-slider-thumb-size / 2) - ($md-slider-track-height / 2));
  transition-duration: $md-transition-time;
  transition-property: left, transform, background;
  width: $md-slider-thumb-size;
  z-index: $md-slider-thumb-z-index;

  &:focus {
    outline: none;
  }
}

.md-slider-thumb--on {
  background: $md-slider-on-color;
}

.md-slider-thumb--active {
  transform: scale($md-slider-thumb-active-scale);
  transform-origin: center;
}

.md-slider-thumb--dragging {
  transition-property: transform, background;
}

.md-slider-thumb--disabled {
  background: $md-slider-off-color;
  transform: scale($md-slider-thumb-disabled-scale);
}

.md-slider-thumb--continuous-off {
  background: $bg-color;
  border: 2px solid;
  border-color: $md-slider-off-color;
  position: relative;
}

.md-slider-thumb--mask {
  transform: scale(1);
  z-index: -1;
}

.md-slider-thumb--mask-inked {
  background: $md-slider-mask-ink-color;
  transform: scale(3.5);
  z-index: $md-slider-thumb-z-index - 1;
}

.md-slider-thumb--mask-disabled {
  background: $bg-color;
  border-radius: initial;
  transform: scale(1.1);
  z-index: $md-slider-thumb-z-index - 1;
}

@if $include-discrete {
  @include react-md-discrete-slider($primary-color, $light-theme);
}

@if $include-editor {
