.md-tooltip--vertical {
  top: 50%;
  transform: translate3d(0, -50%, 0);
}

.md-tooltip--top-active {
  transform: translate3d(-50%, -100%, 0);
}

.md-tooltip--right-active {
  transform: translate3d(100%, -50%, 0);
}

.md-tooltip--bottom-active {
  transform: translate3d(-50%, 100%, 0);
}

.md-tooltip--left-active {
  transform: translate3d(-100%, -50%, 0);
}

.md-ink-container {
  border-radius: inherit;
  height: 100%;
  left: 0;
  overflow: hidden;
  pointer-events: none;
  position: absolute;
  top: 0;
  width: 100%;
  z-index: 1;
}

.md-ink-container--2x {
  height: 200%;
  left: -50%;
  top: -50%;
  width: 200%;
}

.md-ink {
  background: get-color('divider', $light-theme);
  border-radius: 50%;
  display: block;
  opacity: 1;
  position: absolute;
  transform: scale(0);
  z-index: -1;
}

.md-ink--active {
  transition-duration: $md-ink-enter-transition-time;
  transition-property: opacity, transform;
  transition-timing-function: $md-transition-acceleration;
}

.md-ink--expanded {
  transform: scale(.9);
}

.md-ink--pulsing {
  animation-duration: $md-ink-pulse-time;
  animation-iteration-count: infinite;
  animation-name: md-ink-pulse;
  animation-timing-function: $md-transition-standard;
}

.md-ink--leaving {
  opacity: 0;
  transition-duration: .3s;
  transition-timing-function: $md-transition-deceleration;
}

@keyframes md-ink-pulse {
  0% {
    transform: scale(.9);
  }

  66% {
    transform: scale(.85);
  }

  100% {
    transform: scale(.9);
  }
}

@if $include-media {
  @include react-md-menus-media;
}

.md-menu-container {
  position: relative;
}

.md-menu-container--menu-below {
  .md-menu-enter {
    transform: scaleY(0);

    &.md-menu-enter-active {
      transform: scaleY(1);
    }
  }
}

.md-list--menu {
  @include md-box-shadow(2);

  position: absolute;
  top: 0;
  z-index: $md-menu-z-index;
}

.md-list--menu-contained {
  width: 100%;
}

.md-list--menu-scrollable {
  overflow-y: auto;
}

.md-list--menu-tr {
  right: 0;
  transform-origin: 100% 0;
}

.md-list--menu-tl {
  transform-origin: 0 0;
}

.md-list--menu-br {
  right: 0;
  transform-origin: 100% 100%;
}

.md-list--menu-bl {
  transform-origin: 0 100%;
}

.md-list--menu-context {
  transform-origin: 50% 0;
}

.md-list--menu-nested {
  @include md-box-shadow(3);

  position: absolute;
  z-index: $md-menu-z-index + 1;
}

.md-list--menu-below {
  left: 0;
  top: 100%;
  transform-origin: 50% 0;
  width: 100%;
}

.md-menu-enter {
  transform: scale(0);
}

.md-menu-leave {
  opacity: 1;
}

.md-menu-enter-active,
.md-menu-leave-active {
  transition-duration: $md-menu-transition-time;
}

.md-menu-enter.md-menu-enter-active {
  transform: scale(1);
  transition-property: transform;
}

.md-menu-leave.md-menu-leave-active {
  opacity: 0;
  transition-property: opacity;
}

.md-menu-enter.md-menu-enter-active {
  transition-timing-function: $md-transition-deceleration;
}

.md-menu-leave.md-menu-enter-leave {
  transition-timing-function: $md-transition-acceleration;
}

@if $include-cascading {
  @include react-md-menus-cascading;
}

@if $media-included {
  @include react-md-tooltips-media;
}

.md-tooltip-container {
  height: 100%;
  left: 0;
  pointer-events: none;
  position: absolute;
  top: 0;
  user-select: none;
  width: 100%;
  z-index: 1;
}

.md-tooltip {
  align-items: center;
  background: $md-tooltip-color;
  color: $md-white-base;
  display: inline-flex;
  font-weight: $md-font-medium;
  opacity: $md-tooltip-opacity;
  outline: none;
  position: absolute;
  white-space: nowrap;
}

.md-tooltip--active {
  transition-duration: $md-transition-time;
  transition-property: opacity, transform;
}

.md-tooltip--enter,
.md-tooltip--leave-active {
  opacity: 0;
}

.md-tooltip--enter-active {
  opacity: $md-tooltip-opacity;
  transition-timing-function: $md-transition-deceleration;
}

.md-tooltip--leave-active {
  transition-timing-function: $md-transition-acceleration;
}

.md-tooltip--horizontal {
  left: 50%;
  transform: translate3d(-50%, 0, 0);
}

.md-tooltip--vertical {
  top: 50%;
  transform: translate3d(0, -50%, 0);
}

.md-tooltip--top-active {
  transform: translate3d(-50%, -100%, 0);
}

.md-tooltip--right-active {
  transform: translate3d(100%, -50%, 0);
}

.md-tooltip--bottom-active {
  transform: translate3d(-50%, 100%, 0);
}

.md-tooltip--left-active {
  transform: translate3d(-100%, -50%, 0);
}
