@if $light-theme != $md-light-theme {
  .#{$html-class-name} {
    background: get-color('background', $light-theme);

    .md-headline,
    .md-title,
    .md-subheading-2,
    .md-subheading-1,
    .md-body-2,
    .md-body-1 {
      color: get-color('text', $light-theme);
    }

    .md-display-4,
    .md-display-3,
    .md-display-2,
    .md-display-1,
    .md-caption {
      color: get-color('secondary', $light-theme);
    }

    @if $extend-html-tags {
      h6,
      h5,
      h4,
      h3,
      h2,
      h1,
      p {
        color: get-color('text', $light-theme);
      }

      h1,
      caption {
        color: get-color('secondary', $light-theme);
      }

    }

  }
}

