  @include react-md-theme-icons($light-theme);
  @include react-md-theme-inks($light-theme);
  @include react-md-theme-lists($light-theme);
  @include react-md-theme-pickers($primary-color, $light-theme);
  @include react-md-theme-progress($secondary-color, $progress-swatch);
  @include react-md-theme-selection-controls($secondary-color, $light-theme);
  @include react-md-theme-sliders($primary-color, $secondary-color, $light-theme);
  @include react-md-theme-tabs($secondary-color);
  @include react-md-theme-text-fields($primary-color, $light-theme, $error-color);
  @include react-md-theme-toolbars($light-theme);

  @include react-md-theme-colors($primary-color, $secondary-color, $light-theme);
}

$full-font-name: str-replace($font-name, ' ') + get-font-suffix($font-weight);

$fonts-url: if($fonts-url == null, '/fonts/' + to-lower-case(str-replace($font-name, ' ', '-')), $fonts-url);
$fonts-url: if(char-at($fonts-url) != '/', $fonts-url + '/', $fonts-url);

@font-face {
  font-family: quote($font-name);
  font-style: normal;
  font-weight: $font-weight;

  src: local(#{quote($font-name)}), local(#{quote($full-font-name)}), url($fonts-url + $full-font-name + '.ttf') format('truetype');
}

